{% set email_and_website = frappe.get_list("Company", filters={"name": doc.company}, fields=["*"]) %}
{% set company_address = frappe.get_list("Address", filters={"name": doc.company_address}, fields=["*"]) %}
{% set customer_details = frappe.get_list("Address", filters={"link_name": doc.customer}, fields=["*"]) %}

{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}
<table style="width: 100%; border-collapse: collapse;">
    <tbody><tr style="height: 90px;">
        <td style="width: 40.8%; border: 1px solid black; padding: 6px;">
            <div style="display: flex; align-items: center; height: 75px;">
                <div style="width: 68.281px;">
                    <strong>{{display_data(doc.company)}}</strong>
                </div>
                <div style="font-size: 10px; padding-left: 10px;">
                    {% for details in company_address %}
                        {{display_data(details.address_line1)}} {{display_data(details.city)}}-{{display_data(details.pincode)}} Phone: {{display_data(details.phone)}}<br> Email: {{display_data(details.email_id)}}
                    {% endfor %}
                </div>
            </div>
        </td>
        <td style="width: 58.8%; border: 1px solid black; padding: 8px;">
            BUYER {{doc.customer}} <br>
            {% for details in customer_details %}
                {{display_data(details.address_line1)}}, <br> {{display_data(details.state)}}-{{display_data(details.pincode)}}
            {% endfor %}
        </td>
    </tr>
    <tr style="height: 90px;">
        <td style="width: 40.8%; border: 1px solid black; padding: 8px;">
            DBR/AGENT <br>
            {{display_data(doc.sales_partner) }}
        </td>
        <td style="width: 58.8%; border: 1px solid black; padding: 0px !important;">
            <table style="width: 100%; border-collapse: collapse;">
                <tbody><tr style="height: 30px;">
                    <td style="width: 55%; font-size: 12px; border-bottom: 1px solid black;">
                        ORDER FORM No. : {{display_data(doc.name)}}
                    </td>
                    <td style="width: 45%; border-bottom: 1px solid black; padding: 0 !important; padding-top: 10px !important;">
                        DATE: {{display_data(doc.transaction_date)}}
                    </td>
                </tr>
                <tr style="height: 30px;">
                    <td style="width: 55%; border-bottom: 1px solid black;">
                        TRANSPORT : {{display_data(doc.custom_transport)}}
                    </td>
                    <td style="width: 45%; border-bottom: 1px solid black; padding: 0 !important; padding-top: 10px !important;">
                        GSTIN: 
                        {% for details in company_address %}
                            {{display_data(details.gstin)}}
                        {% endfor %}
                    </td>
                </tr>
                <tr style="height: 30px;">
                    <td style="width: 55%;">
                        REMARKS : {{display_data(doc.custom_remark)}}
                    </td>
                    <td style="width: 45%; font-size: 12px; padding: 0 !important; padding-top: 10px !important;">
                        PHONE: 
                        {% for details in customer_details %}
                            {{display_data(details.phone)}}
                        {% endfor %}
                    </td>
                </tr>
            </tbody></table>
        </td>
    </tr>
</tbody></table>

{% set email_and_website = frappe.get_list("Company", filters={"name": doc.company}, fields=["*"]) %}
{% set company_address = frappe.get_list("Address", filters={"name": doc.company_address}, fields=["*"]) %}
<div style="border: 1px solid black; padding: 5px;">
    <div style="width: 30%; flex-direction: column;">
        <strong>GSTNO.: {{doc.company_gstin or "<br>"}}</strong>
    </div>
    <div style="align-items:center;display: flex; flex-direction: column;"> <strong><u>DELIVERY CHELLAN</u></strong></div>
    <div style="align-items:center;display: flex; flex-direction: column;font-size: 20px;"> <strong>MEZZO APPARELS Pvt ltd</strong></div>

     {% for data in company_address %}
    <div style="align-items:center; text-align:center; display: flex; flex-direction: column;font-size: 10px;"> {{data.address_line1 or "<br>"}}<br>{{data.city or ""}} - {{data.pincode or ""}}</div>
    {% endfor %}
    <div style="align-items:center; text-align:center; display: flex; flex-direction: column;font-size: 10px;">
            {% for company in email_and_website %}
                <div style="margin-left: 5px;"><strong><em> {{ company.email or "<br>"}} {{company.website or ""}}</em></strong></div>
            {% endfor %}
        </div>

</div>

{% set delivery_note_data = frappe.db.get_list("Delivery Note", filters={"name": doc.delivery_note}, fields=["*"]) %}
{% set customer_details = [] %}
{% for data in delivery_note_data %}
    {% set details = frappe.db.get_list("Address", filters={"link_name": data.customer}, fields=["*"]) %}
    {% if details %}
        {% set _ = customer_details.append(details) %}
    {% endif %}
{% endfor %}

{% set fetch_agent_names = frappe.get_doc("Delivery Note", doc.delivery_note) %}

{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}
<style>
    @import url('https://fonts.googleapis.com/css2?family=Rye&amp;display=swap');
</style>
<div style="border: 1px solid black;">
    <div style="padding: 6px;">
        <center style="font-family: Rye; font-weight: 600; font-size: 18px;">PACKING SLIP-MEZZO APPARELS PVT LTD.</center>
    </div>
</div>
<div style="display: flex;">
        <div style="display: flex; flex-direction: column; width: 65%; border-right: 1px solid black; border-bottom: 1px solid black; border-left: 1px solid black;">
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 30%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    PARTY
                </div>
                {% for details in delivery_note_data %}
                    <div style="width: 35%; padding: 5px; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                        {{ display_data(details.customer) }}
                    </div>
                {% endfor %}
            </div>
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 30%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    place
                </div>
                {% for details in customer_details %}
                    <div style="width: 70%; padding: 5px; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                        {{ display_data(details[0].address_line1) }}
                    </div>
                {% endfor %}
            </div>
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 30%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: capitalize; font-size: 12px;">
                    No of package
                </div>
                <div style="width: 35%; padding: 5px; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    {{ display_data(doc.custom_bag) }} BAG
                </div>
            </div>
            <div style="display: flex; height: 44px;">
                <div style="width: 30%; padding: 2px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 15px;">
                    agent <br>
                        {% for details in fetch_agent_names.sales_team %}
                            <p style="font-size: 12px; font-weight: 500;">{{display_data(details.sales_person)}}</p>
                        {% endfor %}
                </div>
                <div style="width: 100%; padding: 10px; font-weight: 600; text-transform: uppercase; font-size: 14px;">

                </div>
            </div>
        </div>
        <div style="display: flex; flex-direction: column; width: 35%; border-right: 1px solid black; border-bottom: 1px solid black;">
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 40%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    date
                </div>
                {% for details in delivery_note_data %}
                    <div style="width: 60%; padding: 5px; font-weight: 600; font-size: 12px;">
                        {{ display_data(details.posting_date) }}
                    </div>
                {% endfor %}
            </div>
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 40%; padding: 7px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px; height: 50px;">
                    Checked By
                </div>
                <div style="width: 60%; padding: 7px; font-weight: 600; font-size: 12px; text-transform: uppercase;">
                    {% set fetch_user_fullname = frappe.db.get_list("User", filters={"email": frappe.session.user}, fields = ["*"]) %}
                    {% for data in fetch_user_fullname %}
                        {{data.full_name}}
                    {% endfor %}
                </div>
            </div>
            <div style="display: flex;  height: 50px;">
                <div style="width: 40%; padding: 13.5px 6px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    packed by
                </div>
                <div style="width: 60%; padding: 13.5px 4px; font-weight: 600; font-size: 12px; text-transform: uppercase;">
                    {% for data in fetch_user_fullname %}
                        {{data.full_name}}
                    {% endfor %}
                </div>
            </div>
        </div>
</div>
