{% set email_and_website = frappe.get_list("Company", filters={"name": doc.company}, fields=["*"]) %}
{% set company_address = frappe.get_list("Address", filters={"link_name": doc.company}, fields=["*"]) %}
{% set customer_details = frappe.get_list("Address", filters={"link_name": doc.customer}, fields=["*"]) %}

{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}
<div style="display: flex; justify-content: space-between; height: 90px;">
    <div style="width: 40.8%; border: 1px solid black; display: flex; padding: 6px;">
        <div style="padding: 0px 6px; width: 17%; height: 75px; border-right: 1px solid black;">
            <img src="/files/sample_logo.jpg" style="height: 75px;">
        </div>
        <div style="width: 83%; padding: 0px 6px; height: 75px; display: flex; align-items: center;">
            <div style="display: flex; flex-direction: column;">
                <div style="text-align: right; font-size: 18px; font-weight: 600; letter-spacing: 2px;">meezo</div>
                <div style="text-align: right; font-size: 13px; font-weight: 500; position: relative; top: -9px;letter-spacing: 1px;">ventures</div>
                <div style="text-align: right; font-size: 9px; font-weight: 500; position: relative; top: -12px;">clothing No.</div>
            </div>
            <div style="font-size: 10px; padding-left: 10px;">
                {% for details in company_address %}
                        {{display_data(details.address_line1)}} {{display_data(details.city)}}-{{display_data(details.pincode)}} Phone : {{display_data(details.phone)}} Email : {{display_data(details.email_id)}}
                {% endfor %}
            </div>
        </div>
    </div>
    <div style="width: 58.8%; border: 1px solid black; padding: 8px;">BUYER {{doc.customer}} <br>
        {% for details in customer_details %}
            {{display_data(details.address_line1)}}, <br> {{display_data(details.state)}}-{{display_data(details.pincode)}}
        {% endfor %}
    </div>
</div>
<div style="display: flex; height: 90px; justify-content: space-between;">
    {% for row in doc.sales_team %}
        <div style="width: 40.8%; border-right: 1px solid black; border-bottom: 1px solid black; border-left: 1px solid black; padding: 8px;">DBR/AGENT <br><ul>
            <li>{{display_data(row.sales_person)}}</li>
        </ul></div>
    {% endfor %}
    <div style="width: 58.8%; border-right: 1px solid black; border-bottom: 1px solid black; border-left: 1px solid black; display: flex; flex-direction: column;">
        <div style="width: 100%; display: flex; height: 30px; padding: 3px; justify-content: space-between; border-bottom: 1px solid black;">
            <div style="width: 60%; font-size: 12px;">ORDER FORM No. {{display_data(doc.name)}}</div>
            <div style="width: 40%;">DATE: {{display_data(doc.transaction_date)}}</div>
        </div>
        <div style="width: 100%; display: flex; height: 30px; padding: 3px; justify-content: space-between; border-bottom: 1px solid black;">
            <div style="width: 60%;">TRANSPORT {{display_data(doc.custom_transport)}}</div>
            {% for details in company_address %}
                <div style="width: 40%;">GSTIN: {{display_data(details.gstin)}}</div>
            {% endfor %}
        </div>
        <div style="width: 100%; display: flex; height: 30px; padding: 3px; justify-content: space-between;">
            <div style="width: 60%;">REMARKS</div>
            {% for details in customer_details %}
                <div style="width: 40%; font-size: 12px;">PHONE: {{display_data(details.phone)}}</div>
            {% endfor %}
        </div>
    </div>
</div>

{% set delivery_note_data = frappe.db.get_list("Delivery Note", filters={"name": doc.delivery_note}, fields=["*"]) %}
{% set customer_details = [] %}
{% for data in delivery_note_data %}
    {% set details = frappe.db.get_list("Address", filters={"link_name": data.customer}, fields=["*"]) %}
    {% if details %}
        {% set _ = customer_details.append(details) %}
    {% endif %}
{% endfor %}

{% set fetch_agent_names = frappe.get_doc("Delivery Note", doc.delivery_note) %}

{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}
<style>
    @import url('https://fonts.googleapis.com/css2?family=Rye&amp;display=swap');
</style>
<div style="border: 1px solid black;">
    <div style="padding: 6px;">
        <center style="font-family: Rye; font-weight: 600; font-size: 18px;">PACKING SLIP-MEZZO APPARELS PVT LTD.</center>
    </div>
</div>
<div style="display: flex;">
        <div style="display: flex; flex-direction: column; width: 65%; border-right: 1px solid black; border-bottom: 1px solid black; border-left: 1px solid black;">
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 30%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    PARTY
                </div>
                {% for details in delivery_note_data %}
                    <div style="width: 35%; padding: 5px; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                        {{ display_data(details.customer) }}
                    </div>
                {% endfor %}
            </div>
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 30%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    place
                </div>
                {% for details in customer_details %}
                    <div style="width: 70%; padding: 5px; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                        {{ display_data(details[0].address_line1) }}
                    </div>
                {% endfor %}
            </div>
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 30%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: capitalize; font-size: 12px;">
                    No of package
                </div>
                <div style="width: 35%; padding: 5px; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    {{ display_data(doc.custom_bag) }} BAG
                </div>
            </div>
            <div style="display: flex;">
                <div style="width: 30%; padding: 2px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 15px;">
                    agent <br>
                        {% for details in fetch_agent_names.sales_team %}
                            <p style="font-size: 12px; font-weight: 500;">{{display_data(details.sales_person)}}</p>
                        {% endfor %}
                </div>
                <div style="width: 35%; padding: 10px; font-weight: 600; text-transform: uppercase; font-size: 14px;">

                </div>
            </div>
        </div>
        <div style="display: flex; flex-direction: column; width: 35%; border-right: 1px solid black; border-bottom: 1px solid black;">
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 40%; padding: 5px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    date
                </div>
                {% for details in delivery_note_data %}
                    <div style="width: 60%; padding: 5px; font-weight: 600; font-size: 12px;">
                        {{ display_data(details.posting_date) }}
                    </div>
                {% endfor %}
            </div>
            <div style="display: flex; border-bottom: 1px solid black;">
                <div style="width: 40%; padding: 7px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px; height: 50px;">
                    Checked By
                </div>
                <div style="width: 60%; padding: 7px; font-weight: 600; font-size: 12px; text-transform: uppercase;">
                    {{ display_data(frappe.session.user_fullname) }}
                </div>
            </div>
            <div style="display: flex;  height: 50px;">
                <div style="width: 40%; padding: 13.5px 6px; border-right: 1px solid black; font-weight: 600; text-transform: uppercase; font-size: 12px;">
                    packed by
                </div>
                <div style="width: 60%; padding: 13.5px 4px; font-weight: 600; font-size: 12px; text-transform: uppercase;">
                    {{ display_data(frappe.session.user_fullname) }}
                </div>
            </div>
        </div>
</div>
