css:
* {
    font-family : Helvetica Word;
}
.print-heading {
    display : none;
}
.page-header {
    margin : 0px !important;
    padding : 0px !important;
    position : fixed;
    top : 0;
    width : 100%;
}
.letter-head {
    margin : 0px !important;
}
.page-footer {
    position : fixed;
    bottom : 0;
    width : 100%;
}
.text-center {
    display : none;
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header,
render_field -%}
{% set customer_data = frappe.db.get_list("Address", filters = {"name" : doc.customer_address}, fields=["*"]) %}
{% set fetch_e_Waybill_Log_data = frappe.db.get_list("e-Waybill Log", filters={"reference_name": doc.name},
fields=["*"]) %}
{% set total_qty = doc.items | map(attribute='qty') | sum %}
{% macro
display_data(data) %} {% if data is not none %} {{ data }} {% endif %} {%
endmacro %}
<div class="page-header">
   {{ add_header(0, 1, doc, letter_head, no_letterhead ) }}
</div>
{% set rows_per_page = 28 %}
{% set hsn_page_rows = 22 %}
{% set num_items = doc.items | length %}
{% if num_items <= rows_per_page %} {% set first_page_items=num_items %} {% set second_page_items=0 %} {% set
num_full_pages=1 %} {% else %} {% set first_page_items=min(rows_per_page, num_items) %} {% set
second_page_items=max(0, num_items - rows_per_page) %} {% set num_full_pages=1 + (second_page_items> 0) +
(second_page_items > hsn_page_rows) %}
{% endif %}
<table style="width: 100%; margin-top : 265px;">
   <tbody style="border-left: 1px solid black;">
      <tr style="border: 1px solid black;">
         <th
            style="border: 1px solid black; width: 4%; color: black; text-align: left; padding: 2px !important; font-size: 11px;">
            S N
         </th>
         <th
            style="border: 1px solid black; color: black; width: 55%; text-align: left; padding: 2px !important; font-size: 11px;">
            Description of Goods
         </th>
         <th
            style="border: 1px solid black; color: black; width: 8%; text-align: left; padding: 2px !important; font-size: 11px;">
            HSN/SAC
         </th>
         <th
            style="border: 1px solid black; color: black; width: 5%; text-align: left; padding: 2px !important; font-size: 11px;">
            QTY
         </th>
         <th
            style="border: 1px solid black; color: black; width: 5%; text-align: left; padding: 2px !important; font-size: 11px;">
            Unit
         </th>
         <th
            style="border: 1px solid black; color: black; text-align: left; width: 10.5%; padding: 2px !important; font-size: 11px;">
            Price
         </th>
         <th
            style="border: 1px solid black; color: black; text-align: left; width: 22.5%; padding: 2px !important; font-size: 11px;">
            Amount
         </th>
      </tr>
      {% for i in range(first_page_items) %}
      <tr>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{ i + 1 }}</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["item_code"] }}
         </td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["gst_hsn_code"] }}
         </td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["qty"] }}
         </td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["uom"] }}
         </td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">
            {% set fetching_price = frappe.db.get_list("Item", filters={"name": doc.items[i]["item_code"]},
            fields=["*"]) %}
            {% for data in fetching_price %}
            {{ data.custom_mrp }}
            {% endfor %}
         </td>
         <td
            style="font-size : 11px; border-right : 1px solid black; padding : 3px !important; text-align: right;">
            {{
            doc.items[i]["amount"] }}
         </td>
      </tr>
      {% endfor %}
      {% if first_page_items < hsn_page_rows %} {% for i in range(first_page_items, hsn_page_rows) %} 
      <tr>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
      </tr>
      {% endfor %}
      {% endif %}
   </tbody>
   {% if doc.items | length <= 22 %} 
   <tfoot>
   <tbody style="border : 1px solid black;">
      <tr>
         <td colspan="4"
            style="text-align: right; padding-right : 10px; font-size : 11px;padding: 3px !important;">{{
            display_data(total_qty) }}
         </td>
         <td colspan="2" style="text-align: center; font-size : 11px;padding: 3px !important;">Sub Total</td>
         <td style="font-size: 11px; text-align: right; padding: 3px !important;">{{ display_data(doc.total)
            }}
         </td>
      </tr>
      <tr>
         <td colspan="6" style="font-size : 11px; padding: 3px !important;">
            <div style="display: flex; flex-direction: column">
               <div style="padding-left: 370px">
                  Discount @ &nbsp; &nbsp; &nbsp; {%if doc.additional_discount_percentage == 0.0 %}
                  {%else%}
                  {{doc.additional_discount_percentage}} {%endif%}
               </div>
               {% for tax_data in doc.taxes %}
               <div style="padding-left: 370px">
                  {{ display_data(tax_data.description) }}
                  &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @ &nbsp; &nbsp; &nbsp;
                  &nbsp; {{ display_data(tax_data.rate) }} %
               </div>
               {% endfor %}
               <div style="padding-left: 370px">
                  Rounded Off(+)
               </div>
            </div>
         </td>
         <td style="font-size : 11px; padding: 3px !important;">
            <div style="text-align: right;">{{ doc.discount_amount or "0.0" }}</div>
            <div style="display: flex; flex-direction: column">
               {% for tax_data in doc.taxes %}
               <div style="text-align: right;">{{ display_data(tax_data.tax_amount) }}</div>
               {% endfor %}
               <div style="text-align: right;">{{ display_data(doc.rounding_adjustment) }}</div>
            </div>
         </td>
      </tr>
      <tr>
         <td colspan="6" style="padding : 5px !important">
            <div style="width: 100%; display: flex; justify-content: space-between;">
               <div>
                  <b style="font-size: 11px;">{{ display_data(doc.in_words) }}</b>
               </div>
               <div>
                  <b style="font-size: 11px;">GRAND TOTAL</b>
               </div>
            </div>
         </td>
         <td style="text-align: right; font-size: 11px; padding : 5px !important;">
            {{ display_data(doc.rounded_total) }}
         </td>
      </tr>
   </tbody>
   </tfoot>
   {% endif %}
</table>
{% if num_full_pages > 1 %}
{% for page in range(1, num_full_pages) %}
<table style="width: 100%; page-break-before: always; position : relative; top : 265px !important;">
   <tbody style="border-left : 1px solid black;">
      {% set start_index = rows_per_page + (page - 1) * hsn_page_rows %}
      {% set end_index = rows_per_page + page * hsn_page_rows %}
      {% for i in range(start_index, end_index) %}
      {% if i < num_items %} 
      <tr>
         <td style="width: 4%;font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{ i +
            1 }}
         </td>
         <td style="width: 55%;font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["item_code"] }}
         </td>
         <td style="width: 8%;font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["gst_hsn_code"] }}
         </td>
         <td style="width: 5%;font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["qty"] }}
         </td>
         <td style="width: 5%;font-size : 11px; border-right : 1px solid black; padding : 3px !important">{{
            doc.items[i]["uom"] }}
         </td>
         <td style="width: 10.5%;font-size : 11px; border-right : 1px solid black; padding : 3px !important">
            {% set fetching_price = frappe.db.get_list("Item", filters={"name": doc.items[i]["item_code"]},
            fields=["*"]) %}
            {% for data in fetching_price %}
            {{ data.custom_mrp }}
            {% endfor %}
         </td>
         <td
            style="width: 22.5%;font-size : 11px; border-right : 1px solid black; padding : 3px !important; text-align: right">
            {{
            doc.items[i]["amount"] }}
         </td>
      </tr>
      {% endif %}
      {% endfor %}
      {% for j in range(doc.items | length - start_index,
      hsn_page_rows) %}
      <tr>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
         <td style="font-size : 11px; border-right : 1px solid black; padding : 3px !important">&nbsp;</td>
      </tr>
      {% endfor %}
   </tbody>
   <tfoot>
   <tbody style="border : 1px solid black;">
      <tr>
         <td colspan="4"
            style="text-align: right; padding-right : 10px; font-size : 11px;padding: 3px !important;">{{
            display_data(total_qty) }}
         </td>
         <td colspan="2" style="text-align: center; font-size : 11px;padding: 3px !important;">Sub Total</td>
         <td style="font-size: 11px; text-align: right; padding: 3px !important;">{{ display_data(doc.total)
            }}
         </td>
      </tr>
      <tr>
         <td colspan="6" style="font-size : 11px; padding: 3px !important;">
            <div style="display: flex; flex-direction: column">
               <div style="padding-left: 370px">
                  Discount @ &nbsp; &nbsp; &nbsp; {%if doc.additional_discount_percentage == 0.0 %}
                  {%else%}
                  {{doc.additional_discount_percentage}} {%endif%}
               </div>
               {% for tax_data in doc.taxes %}
               <div style="padding-left: 370px">
                  {{ display_data(tax_data.description) }}
                  &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; @ &nbsp; &nbsp; &nbsp;
                  &nbsp; {{ display_data(tax_data.rate) }} %
               </div>
               {% endfor %}
               <div style="padding-left: 370px">
                  Rounded Off(+)
               </div>
            </div>
         </td>
         <td style="font-size : 11px; padding: 3px !important;">
            <div style="text-align: right;">{{ doc.discount_amount or "0.0" }}</div>
            <div style="display: flex; flex-direction: column">
               {% for tax_data in doc.taxes %}
               <div style="text-align: right;">{{ display_data(tax_data.tax_amount) }}</div>
               {% endfor %}
               <div style="text-align: right;">{{ display_data(doc.rounding_adjustment) }}</div>
            </div>
         </td>
      </tr>
      <tr>
         <td colspan="6" style="padding : 5px !important">
            <div style="width: 100%; display: flex; justify-content: space-between;">
               <div>
                  <b style="font-size: 11px;">{{ display_data(doc.in_words) }}</b>
               </div>
               <div>
                  <b style="font-size: 11px;">GRAND TOTAL</b>
               </div>
            </div>
         </td>
         <td style="text-align: right; font-size: 11px; padding : 5px !important">
            {{ display_data(doc.rounded_total) }}
         </td>
      </tr>
   </tbody>
   </tfoot>
</table>
{% endfor %}
{% endif %}
<!-- Page Footer Section -->
<div class="page-footer">{{footer}}</div>
