css:
* {
    font-family : Helvetica Word;
}
.print-heading {
    display : none;
}
.page-header {
    margin : 0px !important;
    padding : 0px !important;
    position : fixed;
    top : 0;
    width : 100%;
}
.letter-head {
    margin : 0px !important;
}
.page-footer {
    position : fixed;
    bottom : 0;
    width : 100%;
}
.text-center {
    display : none;
}
@page {
    size: A4 portrait;
    margin: 9.9mm;
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set customer_data = frappe.db.get_list("Address", filters={"name": doc.customer_address}, fields=["*"]) %}
{% set fetch_e_Waybill_Log_data = frappe.db.get_list("e-Waybill Log", filters={"reference_name": doc.name},
fields=["*"]) %}
{% set total_qty = doc.items | map(attribute='qty') | sum %}
{% macro display_data(data) %} {% if data is not none %} {{ data }} {% endif %} {% endmacro %}
{% set aggregated_data = {} %}
{% for row in doc.items %}
{% set fetching_parent_items_dets = frappe.db.get_list("Item", filters={"item_code": row.item_code},
fields=["variant_of"]) %}
{% for parent_data in fetching_parent_items_dets %}
{% set parent_items_dets = frappe.db.get_list("Item", filters={"name": parent_data.variant_of},
fields=["custom_product_name", "brand", "gst_hsn_code", "stock_uom"]) %}
{% for data in parent_items_dets %}
{% set custom_mrp = row.custom_custom_mrp %}
{% set product_name = data.custom_product_name %}
{% set brand = data.brand %}
{% set gst_hsn_code = data.gst_hsn_code %}
{% set uom = data.stock_uom %}
{% set key = (custom_mrp, product_name, brand) %}
{% if key not in aggregated_data %}
{% set _ = aggregated_data.update({key: {'custom_mrp': custom_mrp, 'product_name': product_name, 'brand': brand, 'qty':
row.qty, 'total': row.amount, 'hsn_code': gst_hsn_code, 'uom': uom}}) %}
{% else %}
{% set current_data = aggregated_data[key] %}
{% set new_qty = current_data.qty + row.qty %}
{% set new_amt = current_data.total + row.amount %}
{% set _ = aggregated_data[key].update({'qty': new_qty, 'total': new_amt}) %}
{% endif %}
{% endfor %}
{% endfor %}
{% endfor %}
{% set finale_list = aggregated_data.values() | list %}
<div class="page-header">
    {{ add_header(0, 1, doc, letter_head, no_letterhead) }}
</div>
{% set rows_per_page = 28 %}
{% set hsn_page_rows = 25 %}
{% set num_items = finale_list | length %}
{% if num_items <= rows_per_page %} {% set first_page_items=num_items %} {% set second_page_items=0 %} {% set
    num_full_pages=1 %} {% else %} {% set first_page_items=min(rows_per_page, num_items) %} {% set
    second_page_items=max(0, num_items - rows_per_page) %} {% set num_full_pages=1 + (second_page_items> 0) +
    (second_page_items > hsn_page_rows) %}
    {% endif %}
    <table style="width: 100%; margin-top: 265px;">
        <tbody style="border-left: 1px solid black; border-right: 1px solid black">
            <tr style="border: 1px solid black;">
                <th
                    style="border: 1px solid black; width: 4%; color: black; text-align: left; padding: 2px !important; font-size: 11px;">
                    S N
                </th>
                <th
                    style="border: 1px solid black; color: black; width: 55%; text-align: left; padding: 2px !important; font-size: 11px;">
                    Description of Goods
                </th>
                <th
                    style="border: 1px solid black; color: black; width: 8%; text-align: left; padding: 2px !important; font-size: 11px;">
                    HSN/SAC
                </th>
                <th
                    style="border: 1px solid black; color: black; width: 5%; text-align: left; padding: 2px !important; font-size: 11px;">
                    QTY
                </th>
                <th
                    style="border: 1px solid black; color: black; width: 5%; text-align: left; padding: 2px !important; font-size: 11px;">
                    Unit
                </th>
                <th
                    style="border: 1px solid black; color: black; text-align: left; width: 10.5%; padding: 2px !important; font-size: 11px;">
                    Price
                </th>
                <th
                    style="border: 1px solid black; color: black; text-align: right; width: 22.5%; padding: 2px !important; font-size: 11px;">
                    Amount
                </th>
            </tr>
            {% for i in range(first_page_items) %}
            <tr>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">{{ i + 1 }}</td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">{{
                    display_data(finale_list[i].product_name) }} {{ display_data(finale_list[i].brand) }}
                </td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">{{
                    display_data(finale_list[i].hsn_code) }}
                </td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">{{
                    display_data(finale_list[i].qty) | int | abs }}
                </td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">{{
                    display_data(finale_list[i].uom) }}
                </td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">{{
                    display_data(finale_list[i].custom_mrp) }}
                </td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important; text-align: right;">
                    {{
                    display_data(finale_list[i].total) | int | abs }}
                </td>
            </tr>
            {% endfor %}
            {% if first_page_items < hsn_page_rows %} {% for i in range(first_page_items, hsn_page_rows) %} <tr>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">&nbsp;</td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">&nbsp;</td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">&nbsp;</td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">&nbsp;</td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">&nbsp;</td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">&nbsp;</td>
                <td style="font-size: 11px; border-right: 1px solid black; padding: 3px !important">&nbsp;</td>
                </tr>
                {% endfor %}
                {% endif %}
        </tbody>
        {% if finale_list | length <= 25 %} <tfoot>
            <tbody style="border: 1px solid black;">
                <tr style="border-bottom: 1px solid black;">
                    <td colspan="4"
                        style="text-align: right; padding-right: 10px; font-size: 11px; padding: 3px !important; font-weight: 600;">{{
                        display_data(total_qty) | int | abs }}
                    </td>
                    <td colspan="2" style="text-align: center; font-size: 11px; padding: 3px !important; font-weight: 600;">Sub Total</td>
                    <td style="font-size: 11px; text-align: right; padding: 3px !important; font-weight: 600;">{{ display_data(doc.total) | int | abs
                        }}
                    </td>
                </tr>
                <tr>
                    <td colspan="6" style="font-size: 11px; padding: 3px !important;">
                        <div style="display: flex; flex-direction: column">
                            <div style="padding-left: 370px">Discount &nbsp; &nbsp; &nbsp; @ {% if
                                doc.additional_discount_percentage != 0.0 %}% {{ doc.additional_discount_percentage }}
                                {% endif
                                %}
                            </div>
                            {% for tax_data in doc.taxes %}
                            <div style="padding-left: 370px">{{ display_data(tax_data.description) }} &nbsp; &nbsp;
                                &nbsp; &nbsp; &nbsp;&nbsp; @ {{
                                display_data(tax_data.rate) }} %
                            </div>
                            {% endfor %}
                            <div style="padding-left: 370px">Rounded Off(+)</div>
                        </div>
                    </td>
                    <td style="font-size: 11px; padding: 3px !important;">
                        <div style="text-align: right;">{{ (doc.discount_amount) | int | abs or "0.0" }}</div>
                        {% for tax_data in doc.taxes %}
                            <div style="text-align: right;">{{
                                display_data(tax_data.tax_amount) | int | abs }}
                            </div>
                        {% endfor %}
                        <div style="text-align: right;">{{ (doc.rounding_adjustment) | float | abs or "0.0" }}</div>
                    </td>
                </tr>
                <tr style="border-top : 1px solid black">
                    <td colspan="6" style="padding : 5px !important">
                        <div style="width: 100%; display: flex; justify-content: space-between;">
                            <div>
                                <b style="font-size: 11px;">{{ display_data(doc.in_words) }}</b>
                            </div>
                            <div>
                                <b style="font-size: 11px;">GRAND TOTAL</b>
                            </div>
                        </div>
                    </td>
                    <td style="text-align: right; font-size: 11px; padding : 5px !important; font-weight :700">
                        {{ display_data(doc.rounded_total) | int | abs }}
                    </td>
                </tr>
            </tbody>
            </tfoot>
            {% endif %}
    </table>
    {% if finale_list | length > 25 %}
    <table>
        <tfoot>
            <tbody style="border: 1px solid black;">
                <tr style="border-bottom: 1px solid black;">
                    <td colspan="4"
                        style="text-align: right; padding-right: 10px; font-size: 11px; padding: 3px !important; font-weight: 600;">{{
                        display_data(total_qty) }}
                    </td>
                    <td colspan="2" style="text-align: center; font-size: 11px; padding: 3px !important; font-weight: 600;">Sub Total</td>
                    <td style="font-size: 11px; text-align: right; padding: 3px !important; font-weight: 600;">{{ display_data(doc.total) | int | abs
                        }}
                    </td>
                </tr>
                <tr>
                    <td colspan="6" style="font-size: 11px; padding: 3px !important;">
                        <div style="display: flex; flex-direction: column">
                            <div style="padding-left: 370px">Discount &nbsp; &nbsp; &nbsp; @ {% if
                                doc.additional_discount_percentage != 0.0 %} {{ doc.additional_discount_percentage }}%
                                {% endif
                                %}
                            </div>
                            {% for tax_data in doc.taxes %}
                            <div style="padding-left: 370px">{{ display_data(tax_data.description) }} &nbsp; &nbsp;
                                &nbsp; &nbsp; &nbsp;&nbsp; @ {{
                                display_data(tax_data.rate) }} %
                            </div>
                            {% endfor %}
                            <div style="padding-left: 370px">Rounded Off(+)</div>
                        </div>
                    </td>
                    <td style="font-size: 11px; padding: 3px !important;">
                        <div style="text-align: right;">{{ (doc.discount_amount) | int | abs or "0.0" }}</div>
                        {% for tax_data in doc.taxes %}
                            <div style="text-align: right;">{{
                                display_data(tax_data.tax_amount) | int | abs }}
                            </div>
                        {% endfor %}
                        <div style="text-align: right;">{{ (doc.rounding_adjustment) | float | abs or "0.0" }}</div>
                    </td>
                </tr>
                <tr style="border-top : 1px solid black">
                    <td colspan="6" style="padding : 5px !important">
                        <div style="width: 100%; display: flex; justify-content: space-between;">
                            <div>
                                <b style="font-size: 11px;">{{ display_data(doc.in_words) }}</b>
                            </div>
                            <div>
                                <b style="font-size: 11px;">GRAND TOTAL</b>
                            </div>
                        </div>
                    </td>
                    <td style="text-align: right; font-size: 11px; padding : 5px !important; font-weight :700">
                        {{ display_data(doc.rounded_total) | int | abs }}
                    </td>
                </tr>
            </tbody>
        </tfoot>
    </table>
    {% endif %}
    <!-- Page Footer Section -->
    <div class="page-footer">{{footer}}</div>
