html:
{% macro display_data(data) %}
{% if data is not none %}
{{ data }}
{% endif %}
{% endmacro %}

<div
    style="padding-top : 5px;width: 40mm; height: 40mm; border-radius: 10px; border: 1px solid #25212117; background-color: #fff; padding-bottom: 10px; box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);">

    <div
        style="margin-top: 3px; font-size: 9px; font-weight: 600; padding-left: 5px; position: relative; top: -3px; height: 10px;">
        {{ display_data(doc.custom_product_name) }}
    </div>
    <div
        style="font-size: 9px; font-weight: 600; position: relative; top: -2.7px; padding-left: 5px; height: 10px;">
        {{ display_data(doc.item_name) }}
    </div>
    <div
        style="font-size: 9px; font-weight: 600; position: relative; top: -2.2px; padding-left: 5px; height: 10px;">D.
        No: {{ display_data(doc.custom_design_no) }}
    </div>
    {% for data in doc.attributes %}
    {% if data.attribute == "Size" %}
    <div
        style="font-size: 9px; font-weight: 600;position: relative; top: -3px; padding-left: 5px; height: 10px;">{{
        data.attribute }}: {{ display_data(data.attribute_value) }}
    </div>
    {% endif %}
    {% endfor %}
    <div
        style="position: relative; top: -5px; padding-left: 5px; font-size: 11px; font-weight: 600; height: 12px;">MRP:
        Rs {{ display_data(doc.custom_mrp) }}
    </div>
    <div
        style="position: relative; top: -7px; padding-left: 5px; font-size: 8px; font-weight: 600; height: 9px;">Incl
        of All Taxes
    </div>
    <div
        style="position: relative; top: -7px; padding-left: 5px; font-size: 8px; font-weight: 500;">QTY:
        01 N
    </div>
    <div
        style="font-size: 10px; font-weight: 600; position: relative; top: -12px; padding-left: 5px; height: 11px;">

    </div>
    <div style="position: relative; top: -12px;">
        <div
            style="position: relative; top: -1px; font-size: 10px; font-weight: 600; padding-left: 5px;">
            Shade: {% for data in doc.attributes %}
            {% if data.attribute == "Shade" %}
            {{ data.attribute_value or "" }}
            {% endif %}
            {% endfor %}
        </div>
        {% for data in doc.attributes %}
        {% if data.attribute == "Size" %}
        {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
        <div
            style="padding-right: 5px; font-size: 20px; font-weight: 700; position: relative; top: -28px; right: -1px; text-align: right;">
            {% for item in size_ct.item_attribute_values %}
            {% if item.attribute_value == data.attribute_value %}
            {{ display_data(item.abbr) }}
            {% endif %}
            {% endfor %}
        </div>
        {% endif %}
        {% endfor %}
    </div>
    <div style="position: relative; top: -42px; border-radius: 5px;">
        <div
            style="padding-right: 5px; font-size: 20px; font-weight: 700; padding-left: 5px; padding-right: 5px; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;">
            <img src="{{ generate_barcode(doc.custom_barcode) }}"
                alt="{{ doc.custom_barcode }}"
                style="height: 53px; width: 100%; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;" />
        </div>
    </div>
</div>
