html:
{% macro display_data(data) %} {% if data is not none %} {{ data }} {% endif %}
{% endmacro %} {% if doc.custom_format_type == "FG Barcode" %} {% set
items_per_page = 2 %} {% for i in range(0,doc.custom_copy_numbers,
items_per_page) %}

<table>
  <tr>
    {% for j in range(items_per_page) %} {% set index = i + j %} {% if index <
    doc.custom_copy_numbers %}
    <td style="padding: 0 !important">
      <div
        class="row"
        style="margin-top: 0 !important; margin-right: 15px !important"
      >
        <div class="column" style="margin-left: 10px">
          <div style="width: 52mm; height: 52mm;">
            <div
              style="
                margin-top: 3px;
                font-size: 12px;
                font-weight: 600;
                padding-left: 5px;
                height: 15px;
              "
            >
              {{ display_data(doc.custom_product_name) }}
            </div>
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                padding-left: 5px;
                height: 15px;
              "
            >
              {{ display_data(doc.item_name) }}
            </div>
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                padding-left: 5px;
                height: 14px;
              "
            >
              D. No: {{ display_data(doc.custom_design_no) }}
            </div>
            {% for data in doc.attributes %} {% if data.attribute == "Size" %}
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                padding-left: 5px;
                height: 15px;
              "
            >
              {{ data.attribute }}: {{ display_data(data.attribute_value) }}
            </div>
            {% endif %} {% endfor %}
            <div
              style="
                position: relative;
                padding-left: 5px;
                font-size: 18px;
                font-weight: 800;
                height: 20px;
              "
            >
              MRP: Rs {{ display_data(doc.custom_mrp) }}
            </div>
            <div
              style="
                position: relative;
                padding-left: 5px;
                font-size: 10px;
                font-weight: 600;
                height: 13px;
              "
            >
              Incl of All Taxes
            </div>
            <div
              style="
                position: relative;
                padding-left: 5px;
                font-size: 11px;
                font-weight: 500;
              "
            >
              QTY: 01 N
            </div>
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                padding-left: 5px;
                height: 14px;
              "
            ></div>
            <div style="position: relative; top: -2px">
              <div
                style="
                  position: relative;
                  top: -1px;
                  font-size: 10px;
                  font-weight: 600;
                  padding-left: 5px;
                "
              >
                Shade: {% for data in doc.attributes %} {% if data.attribute ==
                "Shade" %} {{ data.attribute_value or "" }} {% endif %} {%
                endfor %}
              </div>
              {% for data in doc.attributes %} {% if data.attribute == "Size" %}
              {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
              <div
                style="
                  padding-right: 5px;
                  font-size: 20px;
                  font-weight: 700;
                  position: relative;
                  top: -28px;
                  text-align: right;
                "
              >
                {% for item in size_ct.item_attribute_values %} {% if
                item.attribute_value == data.attribute_value %} {{
                display_data(item.abbr) }} {% endif %} {% endfor %}
              </div>
              {% endif %} {% endfor %}
            </div>
            <div style="position: relative; border-radius: 5px">
              <div
                style="
                  padding-right: 5px;
                  font-size: 20px;
                  font-weight: 700;
                  padding-left: 5px;
                  padding-right: 5px;
                  border-bottom-left-radius: 5px;
                  border-bottom-right-radius: 5px;
                "
              >
                <img
                  src="{{ generate_barcode(doc.custom_barcode) }}"
                  alt="{{ doc.custom_barcode }}"
                  style="
                    height: 70px;
                    width: 100%;
                    border-bottom-left-radius: 5px;
                    border-bottom-right-radius: 5px;
                  "
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </td>
    {% endif %} {% endfor %}
  </tr>
</table>
<div style="page-break-after: always"></div>

