css:
.page-header {
    position : fixed;
    width : 100%;
    padding-bottom: 0px !important;
    margin : 0 !important;
}
.print-heading, .text-center {
    display : none;
}
.letter-head {
    margin : 0 !important;
}
.print-format th {
    padding : 3px !important;
    color : black !important;
}
@page {
    size: A4 portrait;
    margin : 7mm
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% macro display_data(data) %}
{% if data is not none %}
{{ data }}
{% endif %}
{% endmacro %}
{% set fetch_dn_items = frappe.get_doc("Delivery Note", doc.delivery_note) %}
<div class="page-header">
  {{ add_header(0, 1, doc, letter_head, no_letterhead ) }}
</div>
<table style="position: relative; top: 170.844px; border: 1px solid black; width: 100%;">
  <thead>
    <tr>
      <th style="width: 5%; border-right: 1px solid black; border-bottom: 1px solid black;">Sl No</th>
      <th style="width: 10%; border-right: 1px solid black; border-bottom: 1px solid black;">MRP</th>
      <th style="width: 55%; border-right: 1px solid black; border-bottom: 1px solid black;">PARTICULARS</th>
      <th style="width: 7%; border-right: 1px solid black; border-bottom: 1px solid black;">QTY</th>
      <th style="width: 7%; border-right: 1px solid black; border-bottom: 1px solid black;">RATE</th>
      <th style="width: 16%; border-bottom: 1px solid black;">Order No</th>
    </tr>
  </thead>
  <tbody>
    {% for data in doc.items %}
    {% if loop.index0 % 33 == 0 and not loop.first %}
  </tbody>
</table>
<div style="page-break-after: always;"></div>
<table style="position: relative; top: 170.844px; border: 1px solid black; width: 100%;">
  <thead>
    <tr>
      <th style="width: 5%; border-right: 1px solid black; border-bottom: 1px solid black;">Sl No</th>
      <th style="width: 10%; border-right: 1px solid black; border-bottom: 1px solid black;">MRP</th>
      <th style="width: 55%; border-right: 1px solid black; border-bottom: 1px solid black;">PARTICULARS</th>
      <th style="width: 7%; border-right: 1px solid black; border-bottom: 1px solid black;">QTY</th>
      <th style="width: 7%; border-right: 1px solid black; border-bottom: 1px solid black;">RATE</th>
      <th style="width: 16%; border-bottom: 1px solid black;">Order No</th>
    </tr>
  </thead>
  <tbody>
    {% endif %}
    <tr>
      <td style="border-right: 1px solid black; border-bottom: 1px solid black; padding : 3px !important;">{{ loop.index
        }}</td>
      <td style="border-right: 1px solid black; border-bottom: 1px solid black; padding : 3px !important;">
        {% set fetch_mrp = frappe.get_all("Item", filters = {"item_code" : data.item_code}, fields = ["custom_mrp"]) %}
        {% for data in fetch_mrp %}
        {{ display_data(data.custom_mrp) }}
        {% endfor %}
      </td>
      <td style="border-right: 1px solid black; border-bottom: 1px solid black; padding : 3px !important;">{{
        display_data(data.item_name) }}</td>
      <td style="border-right: 1px solid black; border-bottom: 1px solid black; padding : 3px !important;">{{
        display_data(data.qty) }}</td>
      <td style="border-right: 1px solid black; border-bottom: 1px solid black; padding : 3px !important;">
        {% for data1 in fetch_dn_items.items %}
        {% if data1.item_code == data.item_code %}
        {{ data1.rate }}
        {% endif %}
        {% endfor %}
      </td>
      <td style="border-bottom: 1px solid black; padding : 3px !important;"></td>
    </tr>
    {% endfor %}
  </tbody>
</table>
