css:
* {
    font-family : Helvetica Word;
    font-size : 11px !important;
}
/* Hide the print heading in the print view */
.print-heading {
    display: none;
}
/* Styling for the fixed page footer */
.page-footer {
    position: fixed;
    bottom: 0;
    width: 100%;
    margin: auto;
}
/* Styling for the fixed page header */
.page-header {
    position: fixed;
    top: 0mm;
    display: block;
    width: 100%;
    border-bottom: 0 !important;
    color: #000;
    margin: 0 !important;
    padding: 0 !important;
}

/* Adjust padding for print format preview in a specific context */
.print-format .print-format-preview {
    padding-left: 0px !important;
}
.letter-head {
    margin : 0 !important;
}

/* Define page size and margins for printing */
@page {
    size: A4 portrait;
    /* Set page size to A4 in portrait orientation */
    margin: 7mm;
    /* Set margin to 10mm */
    /*margin-bottom: 55px;*/
    /* Set bottom margin to 55px */
}

/* Media query for print styles */
@media print {
    thead {
        display: table-header-group;
    }

    /* Display table header in print view */
    tfoot {
        display: table-footer-group;
    }


    /* Display page header in print view */
    .page-header {
        display: block;
    }

    /* Adjust padding for print format preview in print view */
    .print-format .print-format-preview {
        padding-left: 0px !important;
        padding-top: 0px !important;
    }

    /* Hide buttons in print view */
    button {
        display: none;
    }

    /* Set body margin to 0 in print view */
    body {
        margin: 0;
    }
}

/* Adjust margin for the page header */
.page-header {
    margin-top: 0px !important;
}

/* Hide a specific element in print view */
.text-center h4 {
    display: none;
}
.text-center {
    display :none;
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% macro display_data(data) %}
{% if data is not none %}
{{ data }}
{% endif %}
{% endmacro %}


{% set list1 = [] %}
{% for item in doc.items %}
    {% set item_doc = frappe.get_doc("Item", item.item_code) %}
    {% if item_doc.variant_of %}
        {% for data in item_doc.attributes %}
            {% set _ = list1.append({
                'Name': item_doc.item_group,
                'variant_of': item_doc.variant_of,
                'attribute_value': data.attribute_value,
                'attribute': data.attribute,
                'item_name': item_doc.item_code,
                'qty': item.qty,
                'rate': item.rate,
                'remarks' : item.custom_swatch,
                'description' : item.custom_description_of_goods_and_shade_nos,
            }) %}
        {% endfor %}
    {% else %}
        {% set _ = list1.append({'Name': item_doc.item_code, 'qty': item.qty, 'rate' : item.rate, 'remarks' : item.custom_swatch, 'description' : item.custom_description_of_goods_and_shade_nos}) %}
    {% endif %}
{% endfor %}

{% set items = frappe.db.get_list("Item Attribute") %}
{% set attribute_values_and_abbreviations = {} %}

{% for item in items %}
    {% set item_doc = frappe.get_doc("Item Attribute", item.name) %}
    {% for child_table_data in item_doc.item_attribute_values %}
        {% set attribute_value = child_table_data.attribute_value %}
        {% set abbr = child_table_data.abbr %}
        {% set _ = attribute_values_and_abbreviations.update({attribute_value: abbr}) %}
    {% endfor %}
{% endfor %}

{% set ls = aggregate_data(list1,attribute_values_and_abbreviations) %}

<div class="page-header">
    {{ add_header(0, 1, doc, letter_head, no_letterhead ) }}
</div>
{% set page_size = 26 %}
{% set total_pages = (ls | length // page_size) + ((ls | length % page_size) > 0) %}

{% for page in range(total_pages) %}
    <table style="position: relative; top : 363px; width : 100%">
        <tbody>
            <tr style="width : 100%">
                <th style="border: 1px solid black; color: black; text-align: left; padding : 0px !important; text-align : center; width : 5%">Sl <br /> No.</th>
                <th style="border: 1px solid black; color: black; width: 40%; text-align: left; padding : 0px !important; text-align : center">Description of Goods & Shade Nos.</th>
                <th style="border: 1px solid black; color: black; width: 10%; text-align: left; padding : 0px !important; text-align : center">Qty Ordered</th>
                <th style="border: 1px solid black; color: black; width: 10%; text-align: left; padding : 0px !important; text-align : center">Rate</th>
                <th style="border: 1px solid black; color: black; text-align: left; width: 35%; padding : 0px !important; text-align : center">Remarks</th>
            </tr>
            {% for i in range(page * page_size, (page + 1) * page_size) %}
                {% if i < ls | length %}
                    <tr>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important">{{ i + 1 }}</td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important">
                            {{ display_data(ls[i].description) }}
                        </td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important">{{ display_data(ls[i].total_qty) }}</td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important">
                            {% if '/' in ls[i].total_qty %}
                                {{ display_data(ls[i].total_rate / ls[i].qty | int) }}
                            {% else %}
                                {{ display_data(ls[i].total_rate / ls[i].total_qty | int) }}
                            {% endif %}
                        </td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important">{{ display_data(ls[i].remarks) }}</td>
                    </tr>
                {% else %}
                    <tr>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important">&nbsp;</td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important"></td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important"></td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important"></td>
                        <td style="border: 1px solid black; color: black; text-align: left; padding : 3px !important"></td>
                    </tr>
                {% endif %}
            {% endfor %}
        </tbody>
    </table>
    {% if not loop.last %}
        <div style="page-break-after: always;"></div>
    {% endif %}
{% endfor %}
<div class="page-footer" style="border : 1px solid black; width : 100%">{{footer}}</div>
