{% from "templates/print_formats/standard_macros.html" import add_header, render_field %}

{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}

{% set grouped_items = {} %}
{% for item in doc.items %}
    {% set rate_key = item.custom_mrpprice_list %}
    {% if rate_key not in grouped_items %}
        {% set _ = grouped_items.update({ rate_key: {'custom_mrpprice_list': item.custom_mrpprice_list, 'price_list_rate': item.price_list_rate, 'gst_hsn_code': item.gst_hsn_code, 'stock_uom': item.stock_uom, 'items': [{'item_code': item.item_code, 'item_name': item.item_name, 'qty': item.qty, 'amount': item.amount}]}}) %}
    {% else %}
        {% set _ = grouped_items[rate_key]['items'].append({'item_code': item.item_code, 'item_name': item.item_name, 'qty': item.qty, 'amount': item.amount}) %}
    {% endif %}
{% endfor %}

{% set final_list = [] %}
{%- for rate_key, data in grouped_items.items() %}
    {%- if data['items'][0].item_code != 'Total' %}
        {%- set total_qty = data['items']|map(attribute='qty')|sum %}
        {%- set total_amount = data['items']|map(attribute='amount')|sum %}
        {%- set _ = final_list.append({'custom_mrpprice_list': data.custom_mrpprice_list, 'price_list_rate': data.price_list_rate, 'gst_hsn_code': data.gst_hsn_code, 'stock_uom': data.stock_uom, 'item_code': data['items'][0].item_code, 'item_name': data['items'][0].item_name, 'qty': total_qty, 'amount': total_amount}) %}
    {%- endif %}
{%- endfor %}

{% set company_bank_details = frappe.db.get_list("Bank Account", filters={"company" : doc.company} , fields=["*"]) %}
{% set variant_totals = {} %}
{% set variant_gst_hsn = {} %}
{% set variant_stock_uom = {} %}
{% set variant_prices = {} %}
{% set variant_sizes = {} %}

{% for row in doc.items %}
    {% set item_data = frappe.get_doc("Item", row.item_code, fields=["*"]) %}
    {% set variant_of = item_data.variant_of %}
    {% set gst_hsn_code = row.gst_hsn_code %}
    {% set uom = item_data.stock_uom %}

    {% if variant_of not in variant_totals %}
        {% set dummy = variant_totals.update({variant_of: 0}) %}
        {% set dummy = variant_gst_hsn.update({variant_of: set()}) %}
        {% set dummy = variant_stock_uom.update({variant_of: set()}) %}
        {% set dummy = variant_prices.update({variant_of: {
            "Total Qty": 0,
            "Total Price List Rate": 0,
            "Total Rate": 0,
            "Total Base Amount": 0,
            "GST HSN Codes": set(),
            "UOM": set()
        }}) %}
        {% set dummy = variant_sizes.update({variant_of: set()}) %}
    {% endif %}

    {% set dummy = variant_totals.update({variant_of: variant_totals.get(variant_of, 0) + row.qty}) %}
    {% set dummy = variant_gst_hsn[variant_of].add(gst_hsn_code) %}
    {% set dummy = variant_stock_uom[variant_of].add(uom) %}
    {% set dummy = variant_prices[variant_of].update({
        "Total Qty": variant_prices[variant_of].get("Total Qty", 0) + row.qty,
        "Total Price List Rate": variant_prices[variant_of].get("Total Price List Rate", 0) + row.price_list_rate,
        "Total Rate": variant_prices[variant_of].get("Total Rate", 0) + row.rate,
        "Total Base Amount": variant_prices[variant_of].get("Total Base Amount", 0) + row.base_amount,
        "GST HSN Codes": variant_gst_hsn[variant_of] if variant_gst_hsn[variant_of] is not none else set(),
        "UOM": variant_stock_uom[variant_of] if variant_stock_uom[variant_of] is not none else set()
    }) %}

    {# Extracting sizes #}
    {% for attr in item_data.attributes %}
        {% if attr.attribute == "Size" %}
            {% set dummy = variant_sizes[variant_of].add(attr.attribute_value) %}
        {% endif %}
    {% endfor %}
{% endfor %}

{% set variant_list = [] %}
{% for variant, data in variant_prices.items() %}
    {% set variant_dict = {
        "Variant_Of": variant, 
        "Total_Qty": data["Total Qty"], 
        "GST_HSN_Codes": ", ".join(list(data["GST HSN Codes"])),
        "UOM": ", ".join(list(data["UOM"])),
        "Price_List_Rate": data["Total Price List Rate"],
        "Rate": data["Total Rate"],
        "Base_Amount": data["Total Base Amount"],
        "Sizes": ", ".join(list(variant_sizes[variant]))  
    } %}
    {% set dummy = variant_list.append(variant_dict) %}
{% endfor %}
{{ final_list }}

<!-- Outer container for the print format with some styling -->
<div class="print-format .print-format-preview" style="padding-left: 0px">
  <!-- Page Header Section -->
  <div class="page-header">
    {{ add_header(0, 1, doc, letter_head, no_letterhead ) }}
  </div>

  <!-- Page Footer Section -->
  <!--<div class="page-footer">{{footer}}</div>-->

  <!-- Table for the entire print format -->
  <!-- Table for the entire print format -->
    <table>
        <!-- Table Header Section -->
        <thead>
            <tr>
                <th>
                    <!-- Placeholder for the fixed-position header -->
                    <div class="page-header-space"></div>
                </th>
            </tr>
            <tr>
                <th style="border: 1px solid black; width: 8%; color: black; text-align: left; font-weight : bold;">Sr No.</th>
                <th style="border: 1px solid black; color: black; width: 100%; text-align: left; font-weight : bold;">Description of Goods</th>
                <th style="border: 1px solid black; color: black; width: 10%; text-align: left; font-weight : bold;">HSN Code</th>
                <th style="border: 1px solid black; color: black; width: 5%; text-align: left; font-weight : bold;">Qty</th>
                <th style="border: 1px solid black; color: black; width: 5%; text-align: left; font-weight : bold;">Unit</th>
                <th style="border: 1px solid black; color: black; text-align: left; width: 12.5%; font-weight : bold;">MRP</th>
                <th style="border: 1px solid black; color: black; text-align: left; width: 12.5%; font-weight : bold;">Rate</th>
                <th style="border: 1px solid black; color: black; text-align: left; width: 22.5%; padding-right : 30px !important; font-weight : bold;">Amount</th>
            </tr>
        </thead>

        <!-- Table Body Section -->
        <tbody style="border-bottom: 1px solid black;">
            {% for i in range(1, final_list|length + 1) %}
                {% if i % 7 == 0 %}
                    </tbody>
                    </table>
                    <!-- Placeholder for the fixed-position footer -->
                    <div class="page-footer-space"></div>
                    <!-- Page break -->
                    <div class="page-break"></div>
                    <table>
                    <thead>
                        <tr>
                            <th>
                                <!-- Placeholder for the fixed-position header -->
                                <div class="page-header-space"></div>
                            </th>
                        </tr>
                        <tr>
                            <th style="border: 1px solid black; width: 8%; color: black; text-align: left;">Sr No.</th>
                            <th style="border: 1px solid black; color: black; width: 50%; text-align: left;">Description of Goods</th>
                            <th style="border: 1px solid black; color: black; width: 15%; text-align: left;">HSN Code</th>
                            <th style="border: 1px solid black; color: black; width: 5%; text-align: left;">Qty</th>
                            <th style="border: 1px solid black; color: black; width: 5%; text-align: left;">Unit</th>
                            <th style="border: 1px solid black; color: black; text-align: left; width: 12.5%;">MRP</th>
                            <th style="border: 1px solid black; color: black; text-align: left; width: 12.5%;">Rate</th>
                            <th style="border: 1px solid black; color: black; text-align: left; width: 22.5%; padding-right : 30px !important;">Amount</th>
                        </tr>
                    </thead>
                    <tbody style="border-bottom: 1px solid black;">
                {% endif %}
                <tr>
                    <td style="border: 1px solid black; color: black; text-align: left;">{{ i }}</td>
                    <td style="border: 1px solid black; color: black; text-align: left;">{{ final_list[i-1]['item_name'] }}</td>
                    <td style="border: 1px solid black; color: black; text-align: left;">{{ final_list[i-1]['gst_hsn_code'] }}</td>
                    <td style="border: 1px solid black; color: black; text-align: left;">{{ final_list[i-1]['qty'] }}</td>
                    <td style="border: 1px solid black; color: black; text-align: left;">{{ final_list[i-1]['stock_uom'] }}</td>
                    <td style="border: 1px solid black; color: black; text-align: left;">{{ final_list[i-1]['custom_mrpprice_list'] }}</td>
                    <td style="border: 1px solid black; color: black; text-align: left;">{{ final_list[i-1]['price_list_rate'] }}</td>
                    <td style="border: 1px solid black; color: black; text-align: left; padding-right : 30px !important;">{{ final_list[i-1]['amount'] }}</td>
                </tr>
            {% endfor %}
        </tbody>
    </table>
</div>
