css:
/* Hide the print heading in the print view */
.print-heading {
    display: none;
}

/* Define height for page header and page header space */
.page-header,
.page-header-space {
    height: 150px;
}

/* Define height for page footer and page footer space */
.page-footer,
.page-footer-space {
    height: 0px;
}

/* Styling for the fixed page footer */
.page-footer {
    position: fixed;
    bottom: 0;
    width: 100%;
    margin: auto;
}

/* Apply a page break before an element with class 'page-break-before' */
.page-break-before {
    page-break-before: always;
}

/* Styling for the fixed page header */
.page-header {
    position: fixed;
    top: 0mm;
    display: block;
    width: 100%;
    border-bottom: 0 !important;
    /* Remove bottom border */
    color: #ffffff;
    /* Text color */
    margin-left: 0px !important;
    padding-left: 0 !important;
}

/* Adjust padding for print format preview in a specific context */
.print-format .print-format-preview {
    padding-left: 0px !important;
}

/* Define page size and margins for printing */
@page {
    size: A4 portrait;
    /* Set page size to A4 in portrait orientation */
    margin: 10mm;
    /* Set margin to 10mm */
    margin-bottom: 55px;
    /* Set bottom margin to 55px */
}

/* Media query for print styles */
@media print {
    thead {
        display: table-header-group;
    }

    /* Display table header in print view */
    tfoot {
        display: table-footer-group;
    }

    /* Display table footer in print view */

    /* Adjust height for page header and page header space in print view */
    .page-header,
    .page-header-space {
        height: 195px;
        border-bottom: 0 !important;
        /* Remove bottom border */
    }

    /* Adjust height for page footer and page footer space in print view */
    .page-header,
    .page-footer-space {
        height: 0px;
    }

    /* Apply page break before specific elements in print view */
    .page-break-before {
        page-break-before: always;
    }

    /* Display page header in print view */
    .page-header {
        display: block;
    }

    /* Adjust padding for print format preview in print view */
    .print-format .print-format-preview {
        padding-left: 0px !important;
        padding-top: 0px !important;
    }

    /* Hide buttons in print view */
    button {
        display: none;
    }

    /* Set body margin to 0 in print view */
    body {
        margin: 0;
    }
}

/* Adjust margin for the page header */
.page-header {
    margin-top: 0px !important;
}

/* Hide a specific element in print view */
.text-center h4 {
    display: none;
}

html:
{% set company_bank_details = frappe.db.get_list("Bank Account", filters={"company" : doc.company} , fields=["*"]) %}
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}

{% macro display_data(data) %}
{% if data is not none %}
{{ data }}
{% endif %}
{% endmacro %}

{% set items = doc.items %}
{% set grouped_items = {} %}

{# Group items by custom_mrp and sum the quantities for each item_code #}
{% for item in items %}
{% set custom_mrp = item.custom_mrp %}
{% set item_code = item.item_code %}
{% set qty = item.qty %}
{% set product_name = item.item_code %}

{% if custom_mrp not in grouped_items %}
{% set _ = grouped_items.update({custom_mrp: {"Product_Name": product_name, "custom_mrp": custom_mrp}}) %}
{% endif %}

{% set item_dict = grouped_items[custom_mrp] %}

{% if item_code not in item_dict %}
{% set _ = item_dict.update({item_code: qty}) %}
{% else %}
{% set _ = item_dict.update({item_code: item_dict[item_code] + qty}) %}
{% endif %}
{% endfor %}

{# Convert the grouped data to the desired format #}
{% set output = [] %}
{% for custom_mrp, item_dict in grouped_items.items() %}
{% set item_dict = item_dict.copy() %}
{% set attributes = {} %}
{% set shade_ls = [] %}

{# Fetch data from Item doctype for each item_code #}
{% for item_code, qty in item_dict.items() if item_code != "Product_Name" and item_code != "custom_mrp" %}
{% set item = frappe.get_doc("Item", item_code) %}
{% for child_table_data in item.attributes %}
{% if child_table_data.attribute == "Size" %}
{% set size_key = child_table_data.attribute_value %}
{% if size_key in attributes %}
{% set _ = attributes.update({size_key: attributes[size_key] + qty}) %}
{% else %}
{% set _ = attributes.update({size_key: qty}) %}
{% endif %}
{% endif %}
{% if child_table_data.attribute == "Shade" %}
{% set _ = shade_ls.append(child_table_data.attribute_value) %}
{% endif %}
{% endfor %}
{% endfor %}

{% set _ = attributes.update({"Shade": shade_ls}) %}
{% set _ = item_dict.update(attributes) %}
{% set _ = output.append(item_dict) %}
{% endfor %}

<!-- {{ output }} -->


<!-- Outer container for the print format with some styling -->
<div class="print-format .print-format-preview" style="padding-left: 0px">
  <!-- Page Header Section -->
  <div class="page-header">
    {{ add_header(0, 1, doc, letter_head, no_letterhead ) }}
  </div>

  <!-- Table for the entire print format -->
  <table>
    <!-- Table Header Section -->
    <thead>
      <tr>
        <th>
          <!-- Placeholder for the fixed-position header -->
          <div class="page-header-space"></div>
        </th>
      </tr>
      <tr>
        <th style="border: 1px solid black; width: 9%; color: black; text-align: center;">Sl No.</th>
        <th style="border: 1px solid black; color: black; width: 10.5%; text-align: center;">MRP</th>
        <th style="border: 1px solid black; color: black; width: 35%; text-align: center;">SERIES</th>
        <th style="border: 1px solid black; color: black; width: 19%; text-align: center;">SHADE NO.</th>
        <th style="border: 1px solid black; color: black; width: 15%; text-align: center;">28</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">30</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">32</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">34</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">36 S</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">38 M</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">40 L</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">42 XL</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">44 2XL</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">46 3XL</th>
      </tr>
    </thead>

    <!-- Table Body Section -->
    <tbody>
      {% for i in range(0, output | length) %}
      {% if i < output|length %} {% set row=output[i] %} <tr>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">{{ i + 1 }}</td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">{{ (output[i].custom_mrp) or
          "" }}</td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {% set variant_item_name = frappe.db.get_list("Item", filters={"name": output[i].Product_Name},
          fields=["*"]) %}
          {% for data in variant_item_name %}
          {% set variant_of = data.variant_of %}
          {% if variant_of %}
          {% set variant_item_name_value = frappe.db.get_value("Item", {"name": variant_of}, "item_name") %}
          {{ variant_item_name_value }}
          {% endif %}
          {% endfor %}
        </td>

        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">{{ output[i].Shade|join(',')
          or "" }}</td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['Small'] | int ) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['(30" / 76 CM)'] | int ) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['(32" / 81 CM)'] | int ) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['(34" / 86 CM)'] | int ) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['S (36" / 90 CM)'] | int ) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['M (38" / 95 CM)'] | int ) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['L (40" / 100 CM)'] | int) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['XL (42" / 105 CM)'] | int) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['2XL (44" / 110 CM)'] | int) or "" }}
        </td>
        <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">
          {{ (output[i]['3XL (46" / 115 CM)'] | int ) or "" }}
        </td>
        </tr>
        {% endif %}
        {% endfor %}
    </tbody>
  </table>

  <!-- Table Footer Section -->
  <tfoot>
    <tr>
      <td style="padding : 0 !important">
        <!-- Placeholder for the fixed-position footer -->
        <div class="page-footer-space"></div>
        <div style="display : flex;">
          <div style="width : 70%; border : 1px solid black;">
            BANK DETAILS:
            {% for details in company_bank_details %}
            {{display_data(doc.company)}} {{display_data(details.bank)}} BANK
            {% endfor %}<br />
            A/C NO.
            {% for details in company_bank_details %}
            {{display_data(details.bank_account_no)}}, IFS CODE: {{display_data(details.branch_code)}}
            {% endfor %}
          </div>
          <div style="width : 30%; border : 1px solid black;">

            <div style="border-bottom: 1px solid black; padding: 1px !important;">

              Delivery Date: {{display_data(doc.delivery_date)}}

            </div>
            <div style="padding: 1px !important;">Order taken by :
              {% for row in doc.sales_team %}
              {{display_data(row.sales_person)}}
              {% endfor %}
            </div>
          </div>
        </div>
        <div style="border : 1px solid black;">
          <strong><u>T & C .</u></strong>
          {{display_data(doc.terms)}}
        </div>
      </td>
    </tr>
  </tfoot>
</div>
