css:
/* Hide the print heading in the print view */
.print-heading {
    display: none;
}

/* Define height for page header and page header space */
.page-header,
.page-header-space {
    height: 150px;
}

/* Define height for page footer and page footer space */
.page-footer,
.page-footer-space {
    height: 0px;
}

/* Styling for the fixed page footer */
.page-footer {
    position: fixed;
    bottom: 0;
    width: 100%;
    margin: auto;
}

/* Apply a page break before an element with class 'page-break-before' */
.page-break-before {
    page-break-before: always;
}

/* Styling for the fixed page header */
.page-header {
    position: fixed;
    top: 0mm;
    display: block;
    width: 100%;
    border-bottom: 0 !important;
    /* Remove bottom border */
    color: #ffffff;
    /* Text color */
    margin-left: 0px !important;
    padding-left: 0 !important;
}

/* Adjust padding for print format preview in a specific context */
.print-format .print-format-preview {
    padding-left: 0px !important;
}

/* Define page size and margins for printing */
@page {
    size: A4 portrait;
    /* Set page size to A4 in portrait orientation */
    margin: 10mm;
    /* Set margin to 10mm */
    margin-bottom: 55px;
    /* Set bottom margin to 55px */
}

/* Media query for print styles */
@media print {
    thead {
        display: table-header-group;
    }

    /* Display table header in print view */
    tfoot {
        display: table-footer-group;
    }

    /* Display table footer in print view */

    /* Adjust height for page header and page header space in print view */
    .page-header,
    .page-header-space {
        height: 210px;
        border-bottom: 0 !important;
        /* Remove bottom border */
    }

    /* Adjust height for page footer and page footer space in print view */
    .page-header,
    .page-footer-space {
        height: 0px;
    }

    /* Apply page break before specific elements in print view */
    .page-break-before {
        page-break-before: always;
    }

    /* Display page header in print view */
    .page-header {
        display: block;
    }

    /* Adjust padding for print format preview in print view */
    .print-format .print-format-preview {
        padding-left: 0px !important;
        padding-top: 0px !important;
    }

    /* Hide buttons in print view */
    button {
        display: none;
    }

    /* Set body margin to 0 in print view */
    body {
        margin: 0;
    }
}

/* Adjust margin for the page header */
.page-header {
    margin-top: 0px !important;
}

/* Hide a specific element in print view */
.text-center h4 {
    display: none;
}

html:
{% set company_bank_details = frappe.db.get_list("Bank Account", filters={"company" : doc.company} , fields=["*"]) %}
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}

{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}

<!-- Outer container for the print format with some styling -->
<div class="print-format .print-format-preview" style="padding-left: 0px">
  <!-- Page Header Section -->
  <div class="page-header">
    {{ add_header(0, 1, doc, letter_head, no_letterhead ) }}
  </div>

  <!-- Table for the entire print format -->
  <table>
    <!-- Table Header Section -->
    <thead>
      <tr>
        <th>
          <!-- Placeholder for the fixed-position header -->
          <div class="page-header-space"></div>
        </th>
      </tr>
      <tr>
        <th style="border: 1px solid black; width: 9%; color: black; text-align: center;">Sl No.</th>
        <th style="border: 1px solid black; color: black; width: 10.5%; text-align: center;">MRP</th>
        <th style="border: 1px solid black; color: black; width: 35%; text-align: center;">SERIES</th>
        <th style="border: 1px solid black; color: black; width: 19%; text-align: center;">SHADE NO.</th>
        <th style="border: 1px solid black; color: black; width: 15%; text-align: center;">28</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">30</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">32</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">34</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">36 S</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">38 M</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">40 L</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">42 XL</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">44 XXL</th>
        <th style="border: 1px solid black; color: black; text-align: center; width: 25%;">QTY</th>
      </tr>
    </thead>

    <!-- Table Body Section -->
    <tbody>
      {% for i in range(0, doc.items | length) %}
        {% if i < doc.items|length %}
          {% set row = doc.items[i] %}
          <tr>
            <td style="border: 1px solid black; width: 9%; color: black; text-align: center;">{{ i }}</td>
            {% set item_code_mrp = frappe.db.get_list("Item", filters = {"name" : row.item_code}, fields = ["*"]) %}
            {% for data in item_code_mrp %}
              <td style="border: 1px solid black; color: black; width: 10.5%; text-align: center;">{{data.custom_mrp}}</td>
            {% endfor %}
            <td style="border: 1px solid black; color: black; width: 35%; text-align: center;">{{ row.item_code }}</td>
            {% set item_shade_data = frappe.get_doc("Item", row.item_code) %}
            {% if item_shade_data %}
              {% for atr in item_shade_data.attributes %}
                {% if atr.attribute == "Shade" %}
                  <td style="border: 1px solid black; color: black; width: 19%; text-align: center;">{{ atr.attribute_value }}</td>
                {% else %}
                  <td style="border: 1px solid black; color: black; width: 19%; text-align: center;"></td>
                {% endif %}
              {% endfor %}
            {% endif %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == "Small" %}
                <td style="border: 1px solid black; color: black; width: 15%; text-align: center;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; width: 15%; text-align: center;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == '(30" / 76 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == '(32" / 81 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == '(34" / 86 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == '(36" / 91 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == '(38" / 96 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == 'L (40" / 100 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == 'XL (42" / 105 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            {% for atr in item_shade_data.attributes %}
              {% if atr.attribute == "Size" and atr.attribute_value == '2XL (44" / 110 CM)' %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">✔️</td>
              {% else %}
                <td style="border: 1px solid black; color: black; text-align: center; width: 25%;"></td>
              {% endif %}
            {% endfor %}
            <td style="border: 1px solid black; color: black; text-align: center; width: 25%;">{{ row.qty }}</td>
          </tr>
        {% endif %}
      {% endfor %}
    </tbody>
  </table>

  <!-- Table Footer Section -->
  <table>
    <tfoot>
      <tr>
        <td>
          <!-- Placeholder for the fixed-position footer -->
          <div class="page-footer-space"></div>
          <tbody>
            <tr>
                <td colspan="6" style = "border : 1px solid black; wdith : 60%;">
                    {% for details in company_bank_details %}
                        BANK DETAILS: {{display_data(doc.company)}} {{display_data(details.bank)}} BANK<br>
                        A/C NO. {{display_data(details.bank_account_no)}}, K.H. ROAD BRANCH, IFS CODE: {{display_data(details.branch_code)}}
                    {% endfor %}
                </td>
                <td colspan="7" style = "border : 1px solid black; width : 40%;">
                    <div style="display: flex; flex-direction: column;">
                        {% set delivery_data = frappe.db.get_list(doctype="Delivery Note", filters={"against_sales_order": doc.name}) %}
                        {% set delivery_note_names = [] %}
                        {% for details in delivery_data %}
                            {% set _ = delivery_note_names.append(display_data(details.name)) %}
                        {% endfor %}
                        <div style="border-bottom: 1px solid black; padding: 1px !important;">
                            Delivery Notes: {{ delivery_note_names | join(', ') }}
                        </div>
                        <div style="padding: 1px !important;">Remarks {{display_data(doc.custom_remark)}}</div>
                    </div>
                </td>
            </tr>
        </tbody>
        </td>
      </tr>
    </tfoot>
  </table>
</div>
