css:
.print-format {
	padding: 0mm !important;
	margin-top: 0mm !important;
  	margin-left: 0mm !important;
  	margin-right: 0mm !important;
}
body {
        margin: 0 !important;
        border: 0 !important;
        padding: 0mm 0mm 0mm !important;
      }

html:
{% set size_quantities = {} %}
{% set size_items = {} %}
{% macro display_data(data) %}
{% if data is not none %}
{{ data }}
{% endif %}
{% endmacro %}
{% set finished_items = doc.items | selectattr("is_finished_item", "eq", true) | list %}
{% for row in finished_items %}
{% set item_details = frappe.get_doc("Item", row.item_code) %}
{% for data in item_details.attributes %}
{% if data.attribute == "Size" %}
{% set size = data.attribute_value %}
{% set qty_int = row.qty|int %}
{% if size not in size_quantities %}
{% set _ = size_quantities.update({size: qty_int}) %}
{% set _ = size_items.update({size: [(row.item_code, qty_int)]}) %}
{% else %}
{% set _ = size_quantities.update({size: size_quantities[size] + qty_int}) %}
{% set _ = size_items[size].append((row.item_code, qty_int)) %}
{% endif %}
{% endif %}
{% endfor %}
{% endfor %}
{% set items_list = [] %}
{% for size, items in size_items.items() %}
{% for item in items %}
{% set item_code = item[0] %}
{% set quantity = item[1] %}
{% for _ in range(quantity) %}
{% set _ = items_list.append(item_code) %}
{% endfor %}
{% endfor %}
{% endfor %}
{% set items_per_page = 2 %}
{% for item_stock in doc.items %}
{% if item_stock.is_finished_item == 1 and doc.stock_entry_type == "Manufacture"%}
{% for i in range(0, items_list|length, items_per_page) %}
<table class = "parent-container" style = "width : 80mm; height : 60mm; margin-left : 3.5mm;">
    <tr>
   {% for j in range(items_per_page) %}
   {% set index = i + j %}
   {% if index < items_list|length %}<td style = "padding : 0 !important; padding-top : 1mm !important; padding-right : 14px !important;">
         <div class="row" style="margin-top: 0 !important; margin-right: 2.5mm !important">
        <div class="column" style="margin-left: 10px;">
   <div style = "width : 38.75mm; height : 40mm; margin-top : 20mm;">
      {% set item_details = frappe.get_doc("Item", items_list[index], fields=["*"]) %}
      <div style="
         font-size: 11px;
         font-weight: 600;
         height: 12px;
         ">
         {{ display_data(item_details.custom_product_name) }}
      </div>
      <div style="
         font-size: 11px;
         font-weight: 600;
         position: relative;
         height: 12px;
         ">
         {{ display_data(item_details.item_name) }}
      </div>
      <div style="
         font-size: 11px;
         font-weight: 600;
         position: relative;
         height: 12px;
         ">
         D. No: {{ display_data(item_details.custom_design_no) }}
      </div>
      {% for data in item_details.attributes %}
      {% if data.attribute == "Size" %}
      <div style="
         font-size: 11px;
         font-weight: 600;
         position: relative;
         height: 12px;
         ">
         {{ data.attribute }}: {{ display_data(data.attribute_value) }}
      </div>
      {% endif %}
      {% endfor %}
      <div style="
         position: relative;
         font-size: 16px;
         font-weight: 700;
         height: 17px;
         ">
         MRP: Rs {{ display_data(frappe.db.get_value("Item Price", filters={"item_code": items_list[index]},
         fieldname="price_list_rate")) }}
      </div>
      <div style="
         position: relative;
         font-size: 10px;
         font-weight: 600;
         height: 11px;
         ">
         Incl of All Taxes
      </div>
      <div style="
         position: relative;
         font-size: 11px;
         font-weight: 500;
         height: 12px;
         ">QTY: 01 N
      </div>
      <div style =" display : inline-block; align-items : flex-end; width : 100% ">
         <div style = "  float : left;width : 75%">
            <div style="
               font-size: 10px;
               font-weight: 600;
               position: relative;
               height: 11px;
               ">
            </div>
            <div style="
               position: relative;
               font-size: 10px;
               font-weight: 600;
               height : 11px;
               ">
               Shade: {% for data in item_details.attributes %}
               {% if data.attribute == "Shade" %}
               {{ data.attribute_value or "" }}
               {% endif %}
               {% endfor %}
            </div>
         </div>
         {% for data in item_details.attributes %}
         {% if data.attribute == "Size" %}
         {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
         <div style = "width : 25%;float : right; text-align : right">
            {% for item in size_ct.item_attribute_values %}
            {% if item.attribute_value == data.attribute_value %}
            {{ display_data(item.abbr) }}
            {% endif %}
            {% endfor %}
         </div>
         {% endif %}
         {% endfor %}
      </div>
      <div style="position: relative; border-radius: 5px;">
         {% set item_barcode_details = frappe.get_doc("Item", items_list[index], fields=["*"]) %}
         <div
            style="padding-right: 5px; font-size: 20px; font-weight: 700; padding-left: 5px; padding-right: 5px; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;">
            <img src="{{ generate_barcode(item_barcode_details.custom_barcode) }}"
               alt="{{ item_barcode_details.custom_barcode }}"
               style="height: 38px; width: 100%; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;" />
         </div>
      </div>
   </div>
   </div>
   </div>
   </td>
   {% endif %}
   {% endfor %}
   </tr>
</table>
<div style="page-break-after: always;"></div>
{% endfor %}
{% endif %}
{% endfor %}
