html:
{% set size_quantities = {} %}
{% set size_items = {} %}
{% macro display_data(data) %}
{% if data is not none %}
{{ data }}
{% endif %}
{% endmacro %}
{% for row in doc.items %}
    {% set item_details = frappe.get_doc("Item", row.item_code) %}
    {% for data in item_details.attributes %}
        {% if data.attribute == "Size" %}
            {% set size = data.attribute_value %}
            {% set qty_int = row.qty|int %}
            {% if size not in size_quantities %}
                {% set _ = size_quantities.update({size: qty_int}) %}
                {% set _ = size_items.update({size: [(row.item_code, qty_int)]}) %}
            {% else %}
                {% set _ = size_quantities.update({size: size_quantities[size] + qty_int}) %}
                {% set _ = size_items[size].append((row.item_code, qty_int)) %}
            {% endif %}
        {% endif %}
    {% endfor %}
{% endfor %}

{% set items_list = [] %}
{% for size, items in size_items.items() %}
    {% for item in items %}
        {% set item_code = item[0] %}
        {% set quantity = item[1] %}
        {% for _ in range(quantity) %}
            {% set _ = items_list.append(item_code) %}
        {% endfor %}
    {% endfor %}
{% endfor %}

{% set items_per_page = 2 %}
{% for item_stock in doc.items %}
{% if item_stock.is_finished_item == 1 %}
{% for i in range(0, items_list|length, items_per_page) %}
    <table>
        <tr>
            {% for j in range(items_per_page) %}
                {% set index = i + j %}
                {% if index < items_list|length %}
                    <td>
                        <div class="row">
                            <div class="column" style="margin-left: 10px;">
                                <div style="width: 40mm; height: 40mm; border-radius: 10px; border: 1px solid #25212117; background-color: #fff; padding-bottom: 10px; box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);">
                                    {% set item_details = frappe.get_doc("Item", items_list[index], fields=["*"]) %}
                                    <div style="margin-top: 3px; font-size: 9px; font-weight: 600; padding-left: 5px; position: relative; top: -3px; height: 10px;">
                                        {{ display_data(item_details.custom_product_name) }}
                                    </div>
                                    <div style="font-size: 9px; font-weight: 600; position: relative; top: -2.7px; padding-left: 5px; height: 10px;">
                                        {{ display_data(item_details.item_name) }}
                                    </div>
                                    <div style="font-size: 9px; font-weight: 600; position: relative; top: -2.2px; padding-left: 5px; height: 10px;">D. No: {{ display_data(item_details.custom_design_no) }}
                                    </div>
                                    {% for data in item_details.attributes %}
                                        {% if data.attribute == "Size" %}
                                            <div style="font-size: 9px; font-weight: 600;position: relative; top: -3px; padding-left: 5px; height: 10px;">{{ data.attribute }}: {{ display_data(data.attribute_value) }}
                                            </div>
                                        {% endif %}
                                    {% endfor %}
                                    <div style="position: relative; top: -5px; padding-left: 5px; font-size: 11px; font-weight: 600; height: 12px;">MRP: Rs {{ display_data(frappe.db.get_value("Item Price", filters={"item_code": items_list[index]}, fieldname="price_list_rate")) }}
                                    </div>
                                    <div style="position: relative; top: -7px; padding-left: 5px; font-size: 8px; font-weight: 600; height: 9px;">Incl of All Taxes
                                    </div>
                                    <div style="position: relative; top: -7px; padding-left: 5px; font-size: 8px; font-weight: 500;">QTY: 01 N
                                    </div>
                                    <div style="font-size: 10px; font-weight: 600; position: relative; top: -12px; padding-left: 5px; height: 11px;">

                                    </div>
                                    <div style="position: relative; top: -12px;">
                                        <div style="position: relative; top: -1px; font-size: 10px; font-weight: 600; padding-left: 5px;">
                                            Shade: {% for data in item_details.attributes %}
                                                {% if data.attribute == "Shade" %}
                                                    {{ data.attribute_value or "" }}
                                                {% endif %}
                                            {% endfor %}
                                        </div>
                                        {% for data in item_details.attributes %}
                                            {% if data.attribute == "Size" %}
                                                {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
                                                <div style="padding-right: 5px; font-size: 20px; font-weight: 700; position: relative; left: 0; top: -24px; text-align: right;">
                                                    {% for item in size_ct.item_attribute_values %}
                                                        {% if item.attribute_value == data.attribute_value %}
                                                            {{ display_data(item.abbr) }}
                                                        {% endif %}
                                                    {% endfor %}
                                                </div>
                                            {% endif %}
                                        {% endfor %}
                                    </div>
                                    <div style="position: relative; top: -42px; border-radius: 5px;">
                                        {% set item_barcode_details = frappe.get_doc("Item", items_list[index], fields=["*"]) %}
                                        <div style="padding-right: 5px; font-size: 20px; font-weight: 700; padding-left: 5px; padding-right: 5px; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;">
                                            <img src="{{ generate_barcode(item_barcode_details.custom_barcode) }}" alt="{{ item_barcode_details.custom_barcode }}" style="height: 53px; width: 100%; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;" />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </td>
                {% endif %}
            {% endfor %}
        </tr>
    </table>
    <div style="page-break-after: always;"></div>
{% endfor %}
{% endif %}
{% endfor %}
