html:
{% set total_items = doc.items | length %}
{% set counter = 0 %}
{% for item in doc.items %}
    {% set counter = counter + 1 %}
    {% set qty = item.qty | int %}
    {% for i in range(qty) %}
        <div style="width: 290px;height : 430px; border-radius: 10px; border: 1px solid #25212117; background-color: #fff5f5; padding-bottom: 10px; box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);">
            <div style="background-color: #474747; border-top-right-radius: 10px; border-top-left-radius: 10px; padding-left: 10px; padding-top: 6px; padding-bottom: 8px;">
                <p style="font-size: 10px; margin-bottom: 0; border: 0; color: #dfdad6">Manufactured by</p>
                <p style="font-weight: 600; border: 0; font-size: 16px; color: #fcf489; margin: 0;">Mezzo Ventures</p>
                <p style="width: 220px; border: 0; color: #e5d2a0; line-height: 14px; font-size: 12px;">#14, 1st 'C' Cross, Sudamanagar, Lalbagh Road, Bangalore - 560027 Customer Care: 080 4124 1281 Email: mezzoventures@gmail.com</p>
            </div>
            <div style="display: flex; justify-content: space-between; padding: 5px;">
                <div style="display: flex; flex-direction: column;">
                    <p style="font-size: 18px; font-weight: 600; margin-bottom: 0;">TEENPEOPLE</p>
                    <p style="font-size: 10px; font-weight: 500; margin-top: 0; letter-spacing: 5px; padding-left: 2px; position: relative; top: -5px;">SHIRTOLOGY</p>
                </div>
                <div style="width: 156px">
                    <p style="margin-top:10px;"></p>
                    <p style="font-size: 12px; font-weight: 500; text-align: right; margin: 0;">ORIGIN-INDIA</p>
                    <p style="font-size: 8px; font-weight: 500; text-align: right; margin: 0;">www.meezoventures.com</p>
                </div>
            </div>
            <div style="padding-left: 6px;">
                &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733;
            </div>
            <div style="margin-top: 14px; font-size: 18px; font-weight: 600; letter-spacing: 1px; padding-left: 10px; color: black; font-family : Vidaloka;">{{ item.item_code }} </div>
            <div style="font-size: 16px; font-weight: 600; font-family: Inter; position: relative; top: -3px; padding-left: 10px; color : black;">TP # 120</div> 
            {%- set item_details = frappe.get_doc("Item", item.item_code , fields=["*"]) -%}
            {% for data in item_details.attributes %}
                {% if data.attribute == "Size" %}
                    <div style="font-size: 16px; font-weight: 600; font-family: FontAwesome; position: relative; top: -6px; padding-left: 10px; color: black;">{{ data.attribute }}: {{ data.attribute_value }}</div>
                {% endif %}
            {% endfor %}
            <div style = "font-size: 24px; color : black; font-weight : 600; padding-left : 10px; position : relative ; top : -11px; font-family : Inter">MRP:Rs {{ frappe.db.get_value("Item Price", filters={"item_code": item.item_code}, fieldname="price_list_rate") }}</div>
            <div style = "font-size : 14px; font-family : Inter; font-weight : 600; padding-left : 10px; position : relative; top : -20px;">Incl of All Taxes</div>
            <div style="display: flex; justify-content : space-between; position : relative; top : -15px; align-items : flex-end;">
            {% for data in item_details.attributes %}
                    {% if data.attribute == "Shade" %}
                        <div style="font-size: 16px; font-weight: 700; font-family: FontAwesome; position: relative; top: -8px; padding-left: 10px; color: black;">{{ data.attribute | upper }} : {{ data.attribute_value }}</div>
                    {% endif %}
                    {% if data.attribute == "Size" %}
                        <div style="order : 2;font-size: 20px; font-weight: 700; font-family: FontAwesome; position: relative; top: -8px; padding-right: 10px; color: black;">
                            {{ (data.attribute_value | string)[:1] }}
                        </div>
                    {% endif %}
            {% endfor %}
            </div>
        </div>
        {% if not loop.last or counter != total_items %}
            <div style="page-break-after: always;"></div>
        {% endif %}
    {% endfor %}
{% endfor %}
