html:
{% set total_items = doc.items | length %}
{% set counter = 0 %}
{% for item in doc.items %}
    {% set counter = counter + 1 %}
    {% set qty = item.qty | int %}
    {% for i in range(qty) %}
        <div style="width: 170px;height : 160px; border-radius: 10px; border: 1px solid #25212117; background-color: #fff5f5; padding-bottom: 10px; box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);">
            <div style="display: flex; justify-content: space-between; padding-right: 5px !important; padding-left : 5px !important; ">
                <div style="display: flex; flex-direction: column;">
                    <p style="font-size: 8px; font-weight: 600; margin-bottom: 0; letter-spacing:0.5px;">TEENPEOPLE</p>
                    <p style="font-size: 6px; font-weight: 500; margin-top: 0; letter-spacing: 2px; position: relative; top: -3px;">SHIRTOLOGY</p>
                </div>
                <div style="width: 84px">
                    <!--<p style="margin-top:10px;"></p>-->
                    <p style="font-size: 6px; font-weight: 500; text-align: right; margin: 0; position : relative; top : 6px;">ORIGIN-INDIA</p>
                    <p style="font-size: 6px; font-weight: 500; text-align: right; margin: 0; position : relative; top : 2.4px;">www.meezoventures.com</p>
                </div>
            </div>
            <div style="padding-right: 5px; padding-left : 5px;  position : relative; top : -10px;">
                &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733;
            </div>
            <div style="margin-top: -10px; font-size: 13px; font-weight: 600; font-family: FontAwesome; letter-spacing: 1px; padding-left: 5px; position ; relative; top : -3px;">{{ item.item_name }} </div>
            <div style="font-size: 11px; font-weight: 600; font-family: Inter; position: relative; top: -2.7px; padding-left: 5px;">{{ item.custom_design_no }}</div>
            {%- set item_details = frappe.get_doc("Item", item.item_code , fields=["*"]) -%}
            {% for data in item_details.attributes %}
                {% if data.attribute == "Size" %}
                    <div style="font-size: 11px; font-weight: 600; font-family: FontAwesome; position: relative; top: -6px; padding-left: 5px;">{{ data.attribute }}: {{ data.attribute_value }}</div>
                {% endif %}
            {% endfor %}
            <div style = "position: relative; top: -11px; padding-left: 5px; font-size: 14px; font-weight: 600;">MRP:Rs {{ frappe.db.get_value("Item Price", filters={"item_code": item.item_code}, fieldname="price_list_rate") }}</div>
            <div style = "position: relative; top: -16px; padding-left: 5px; font-size: 9px; font-weight: 600;">Incl of All Taxes</div>
            <div style = "position: relative; top: -19px; padding-left: 5px; font-size: 9px; font-weight: 500;">Comm. {{ item.item_group }}, QTY : 01 N</div>
            <div style = "font-size: 10px;font-weight: 600; position : relative; top : -22px; padding-left : 5px;">JS-421</div>
            <div style="display: flex; justify-content: space-between; position: relative; top: -35px; align-items: flex-end;">
                {% set shade_rendered = false %}

                {% for data in item_details.attributes %}
                    {% if data.attribute == "Shade" %}
                        {% set shade_rendered = true %}
                        <div style="position: relative; top: -3px; font-size: 10px; font-weight: 600; padding-left : 5px;">
                            {{ data.attribute | upper }} : {{ data.attribute_value or "N/A" }}
                        </div>
                    {% endif %}
                {% endfor %}

                {% if not shade_rendered %}
                    <div style="position: relative; top: -3px; font-size: 10px; font-weight: 600; padding-left : 5px;">
                        SHADE : N/A
                    </div>
                {% endif %}

                {% for data in item_details.attributes %}
                    {% if data.attribute == "Size" %}
                        {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
                        <div style="padding-right: 5px; font-size: 20px; font-weight: 700;">
                            {% for item in size_ct.item_attribute_values %}
                                {% if item.attribute_value == data.attribute_value %}
                                    {{ item.abbr }}
                                {% endif %}
                            {% endfor %}
                        </div>
                    {% endif %}
                {% endfor %}
            </div>
            <div style = "position : relative; top : -37px; border-radius : 5px;">
                {%- set item_barcode_details = frappe.get_doc("Item", item.item_code , fields=["*"]) -%}
                <div style="padding-right: 5px; font-size: 20px; font-weight: 700; padding-left : 5px; padding-right : 5px; ">
                        {% for item in item_barcode_details.barcodes %}
                            <img src = "data:image/png;base64, {{item.custom_barcode_image}}" style = "height : 25px; width : 100%"/>
                        {% endfor %}
                </div>
            </div>
        </div>
        {% if not loop.last or counter != total_items %}
            <div style="page-break-after: always;"></div>
        {% endif %}
    {% endfor %}
{% endfor %}
