html:
{% set qty_from_bin = frappe.get_list("Bin", filters={"item_code" : doc.name}, fields = ["*"]) %}
{% set total_qty = qty_from_bin | map(attribute='actual_qty') | sum | int %}
{% set counter = 0 %}
{% macro display_data(data) %}
{% if data is not none %}
{{ data }}
{% endif %}
{% endmacro %}
{% if doc.custom_format_type == "FG Barcode" %}
{% set
items_per_page = 2 %} {% for i in range(0,total_qty,
items_per_page) %}

<table class="parent-container" style="width : 80mm; height : 60mm; margin-left : 3.5mm;">
  <tr>
    {% for j in range(items_per_page) %} {% set index = i + j %} {% if index < total_qty %} <td
      style="padding : 0 !important; padding-top : 1mm !important; padding-right : 14px !important;">
      <div class="row" style="margin-top: 0 !important; margin-right: 2.5mm !important">
        <div class="column" style="margin-left: 10px">
          <div style="width : 38.75mm; height : 40mm; padding-top : 20mm !important;">
            <div style="
              font-size: 11px;
              font-weight: 600;
              height: 12px;
              ">
              {{ display_data(doc.custom_product_name) }}
            </div>
            <div style="
               font-size: 11px;
         font-weight: 600;
         position: relative;
         height: 12px;
              ">
              {{ display_data(doc.item_name) }}
            </div>
            <div style="
               font-size: 11px;
         font-weight: 600;
         position: relative;
         height: 12px;
              ">
              D. No: {{ display_data(doc.custom_design_no) }}
            </div>
            {% for data in doc.attributes %} {% if data.attribute == "Size" %}
            <div style="
              font-size: 11px;
         font-weight: 600;
         position: relative;
         height: 12px;
              ">
              {{ data.attribute }}: {{ display_data(data.attribute_value) }}
            </div>
            {% endif %} {% endfor %}
            <div style="
               position: relative;
         font-size: 16px;
         font-weight: 700;
         height: 17px;
              ">
              MRP: Rs {{ display_data(doc.custom_mrp) }}
            </div>
            <div style="
         position: relative;
         font-size: 10px;
         font-weight: 600;
         height: 11px;
              ">
              Incl of All Taxes
            </div>
            <div style="
         position: relative;
         font-size: 11px;
         font-weight: 500;
         height: 12px;
              ">
              QTY: 01 N
            </div>
            <div style=" display : inline-block; align-items : flex-end; width : 100% ">
              <div style="  float : left;width : 75%">
                <div style="
               font-size: 10px;
               font-weight: 600;
               position: relative;
               height: 11px;
               ">
                </div>
                <div style="
               position: relative;
               font-size: 10px;
               font-weight: 600;
               height : 11px;
               ">
                  Shade: {% for data in doc.attributes %} {% if data.attribute ==
                  "Shade" %} {{ data.attribute_value or "" }} {% endif %} {%
                  endfor %}
                </div>
              </div>
              {% for data in doc.attributes %}
              {% if data.attribute == "Size" %}
              {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
              <div style="width : 25%;float : right; text-align : right">
                {% for item in size_ct.item_attribute_values %} {% if
                item.attribute_value == data.attribute_value %} {{
                display_data(item.abbr) }} {% endif %} {% endfor %}
              </div>
              {% endif %}
              {% endfor %}
            </div>
            <div style="position: relative; border-radius: 5px">
              <div style="
                  padding-right: 5px; font-size: 20px; font-weight: 700; padding-left: 5px; padding-right: 5px; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;
                ">
                <img src="{{ generate_barcode(doc.custom_barcode) }}" alt="{{ doc.custom_barcode }}"
                  style="height: 38px; width: 100%; border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;" />
              </div>
            </div>
          </div>
        </div>
      </div>
      </td>
      {% endif %} {% endfor %}
  </tr>
</table>
<div style="page-break-after: always"></div>

{% endfor %}
{% elif doc.custom_format_type == "Material Barcode" %}
{% for x in range(0,total_qty) %}
<div style="width : 38.75mm; height : 40mm; padding-top : 20mm !important; padding-left : 3.5mm">
  <div style="
              font-size: 11px;
