html:
{% set qty_from_bin = frappe.get_list("Bin", filters={"item_code" : doc.name}, fields = ["*"]) %}
{% set total_qty = qty_from_bin | map(attribute='actual_qty') | sum | int %}
{% set counter = 0 %}
{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}
{% for i in range(total_qty) %}
    {% set counter = counter + 1 %}
        <div style="max-width: 170px; border-radius: 10px; border: 1px solid #25212117; box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); height: 160px;">
    <div style="display: flex; justify-content: space-between; padding-right: 5px !important; padding-left : 5px !important; ">
        <div style="display: flex; flex-direction: column;">
            <p style="font-size: 8px; font-weight: 600; margin-bottom: 0; letter-spacing:0.5px;">TEENPEOPLE</p>
            <p style="font-size: 6px; font-weight: 500; margin-top: 0; letter-spacing: 2px; position: relative; top: -3px;">SHIRTOLOGY</p>
        </div>
        <div style="width: 84px">
            <!--<p style="margin-top:10px;"></p>-->
            <p style="font-size: 6px; font-weight: 500; text-align: right; margin: 0; position : relative; top : 6px;">ORIGIN-INDIA</p>
            <p style="font-size: 6px; font-weight: 500; text-align: right; margin: 0; position : relative; top : 2.4px;">www.meezoventures.com</p>
        </div>
    </div>
    <div style="padding-right: 5px; padding-left : 5px;  position : relative; top : -10px;">
        &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733; &#9733;
    </div>
    <div style="margin-top: -10px; font-size: 13px; font-weight: 600; font-family: FontAwesome; letter-spacing: 1px; padding-left: 5px; position ; relative; top : -3px;">{{ doc.item_name }}</div>
    <div style="font-size: 11px; font-weight: 600; font-family: Inter; position: relative; top: -2.7px; padding-left: 5px;">{{ display_data(doc.custom_design_no) }}</div>
    {%- for row in doc.attributes -%}
        {% if row.attribute == "Size" %}
                <div style="font-size: 11px; font-weight: 600; font-family: FontAwesome; position: relative; top: -6px; padding-left: 5px;">{{ display_data(row.attribute) }}: {{ display_data(row.attribute_value) }}</div>
        {% endif %}
    {% endfor %}
    <div style = "position: relative; top: -11px; padding-left: 5px; font-size: 14px; font-weight: 600;">MRP:Rs {{ display_data(doc.custom_mrp) }}</div>
    <div style = "position: relative; top: -16px; padding-left: 5px; font-size: 9px; font-weight: 600;">Incl of All Taxes</div>
    <div style = "position: relative; top: -19px; padding-left: 5px; font-size: 9px; font-weight: 500;">Comm. {{ display_data(doc.item_group) }}, QTY : 01 {{ display_data(doc.stock_uom[:1]) }}</div>
    <div style = "display : flex; padding-left : 5px; height : 5px; justify-content: space-between;position: relative;top: -21px;">
        <div style = "display : flex; flex-direction : column; min-width: 85px; gap : 2px;">
            <div style = "font-size: 10px;font-weight: 600;">JS-421</div>
            {%- set shade_present = false -%}
            {%- for row in doc.attributes -%}
                {% if row.attribute == "Shade" %}
                    {%- set shade_present = true -%}
                    <div style="position: relative; top: -3px; font-size: 10px; font-weight: 600;">
                        SHADE: {{ display_data(row.attribute_value) }}
                    </div>
                {% endif %}
            {%- endfor %}

            {% if not shade_present %}
                <div style="position: relative; top: -3px; font-size: 10px; font-weight: 600;">
                    SHADE: N/A
                </div>
            {% endif %}
        </div>
        {% for row in doc.attributes %}
                    {% if row.attribute == "Size" %}
                        {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
                        <div style="padding-right: 5px; font-size: 20px; font-weight: 700;">
                            {% for item in size_ct.item_attribute_values %}
                                {% if item.attribute_value == row.attribute_value %}
                                    {{ item.abbr }}
                                {% endif %}
                            {% endfor %}
                        </div>
                    {% endif %}
        {% endfor %}
    </div>
    <div style = "padding-left : 8px; padding-right : 8px; position : relative; top : 0px;">
    {% for details in doc.barcodes %}
        <img src = "data:image/png;base64, {{display_data(details.custom_barcode_image)}}" style = "height : 25px; width : 100%"/>
    {% endfor %}
    </div>
</div>
</div>
{% if not loop.last or counter != total_qty %}
            <div style="page-break-after: always;"></div>
        {% endif %}
{% endfor %}
