html:
{% set qty_from_bin = frappe.get_list("Bin", filters={"item_code" : doc.name}, fields = ["*"]) %}
{% set total_qty = qty_from_bin | map(attribute='actual_qty') | sum | int %}
{% set counter = 0 %}
{% macro display_data(data) %}
    {% if data is not none %}
        {{ data }}
    {% endif %}
{% endmacro %}
{% if doc.custom_format_type == "FG Barcode" %}
{% set
items_per_page = 2 %} {% for i in range(0,total_qty,
items_per_page) %}

<table>
  <tr>
    {% for j in range(items_per_page) %} {% set index = i + j %} {% if index <
    total_qty %}
    <td style="padding: 0 !important">
      <div
        class="row"
        style="margin-top: 0 !important; margin-right: 15px !important"
      >
        <div class="column" style="margin-left: 10px">
          <div style="width: 52mm; height: 52mm; padding-bottom: 10px">
            <div
              style="
                margin-top: 3px;
                font-size: 12px;
                font-weight: 600;
                padding-left: 5px;
                position: relative;
                top: -3px;
                height: 12px;
              "
            >
              {{ display_data(doc.custom_product_name) }}
            </div>
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                top: 0.3px;
                padding-left: 5px;
                height: 12px;
              "
            >
              {{ display_data(doc.item_name) }}
            </div>
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                top: 1.8px;
                padding-left: 5px;
                height: 10px;
              "
            >
              D. No: {{ display_data(doc.custom_design_no) }}
            </div>
            {% for data in doc.attributes %} {% if data.attribute == "Size" %}
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                top: 2px;
                padding-left: 5px;
                height: 10px;
              "
            >
              {{ data.attribute }}: {{ display_data(data.attribute_value) }}
            </div>
            {% endif %} {% endfor %}
            <div
              style="
                position: relative;
                top: 2px;
                padding-left: 5px;
                font-size: 18px;
                font-weight: 800;
                height: 12px;
              "
            >
              MRP: Rs {{ display_data(doc.custom_mrp) }}
            </div>
            <div
              style="
                position: relative;
                top: 9px;
                padding-left: 5px;
                font-size: 10px;
                font-weight: 600;
                height: 9px;
              "
            >
              Incl of All Taxes
            </div>
            <div
              style="
                position: relative;
                top: 11px;
                padding-left: 5px;
                font-size: 11px;
                font-weight: 500;
              "
            >
              QTY: 01 N
            </div>
            <div
              style="
                font-size: 12px;
                font-weight: 600;
                position: relative;
                top: 7px;
                padding-left: 5px;
                height: 11px;
              "
            ></div>
            <div style="position: relative; top: 5px">
              <div
                style="
                  position: relative;
                  top: -1px;
                  font-size: 10px;
                  font-weight: 600;
                  padding-left: 5px;
                "
              >
                Shade: {% for data in doc.attributes %} {% if data.attribute ==
                "Shade" %} {{ data.attribute_value or "" }} {% endif %} {%
                endfor %}
              </div>
              {% for data in doc.attributes %} {% if data.attribute == "Size" %}
              {% set size_ct = frappe.get_doc("Item Attribute", "Size") %}
              <div
                style="
                  padding-right: 5px;
                  font-size: 20px;
                  font-weight: 700;
                  position: relative;
                  top: -28px;
                  text-align: right;
                "
              >
                {% for item in size_ct.item_attribute_values %} {% if
                item.attribute_value == data.attribute_value %} {{
                display_data(item.abbr) }} {% endif %} {% endfor %}
              </div>
              {% endif %} {% endfor %}
            </div>
            <div style="position: relative; top: -23px; border-radius: 5px">
              <div
                style="
                  padding-right: 5px;
                  font-size: 20px;
                  font-weight: 700;
                  padding-left: 5px;
                  padding-right: 5px;
                  border-bottom-left-radius: 5px;
                  border-bottom-right-radius: 5px;
                "
              >
                <img
                  src="{{ generate_barcode(doc.custom_barcode) }}"
                  alt="{{ doc.custom_barcode }}"
                  style="
                    height: 70px;
                    width: 100%;
                    border-bottom-left-radius: 5px;
                    border-bottom-right-radius: 5px;
                  "
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </td>
    {% endif %} {% endfor %}
  </tr>
</table>
<div style="page-break-after: always"></div>

{% endfor %}
{% elif doc.custom_format_type == "Material Barcode" %}
{% for x in range(0,total_qty) %}
<div style="width: 52mm; height: 52mm; padding-bottom: 10px">
  <div
